import React, { useState } from "react";
import "./Login.css"; // Reuse the same neumorphic styling

const PaymentPage = () => {
  // States
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [cardName, setCardName] = useState("");
  const [cardNumber, setCardNumber] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");
  const [message, setMessage] = useState("");

  // Submit handler
  const handleSubmit = (e) => {
    e.preventDefault();
    if (!fullName || !email || !age || !cardName || !cardNumber || !expiry || !cvv) {
      setMessage("⚠️ Please fill in all required fields.");
      return;
    }
    setMessage("Payment successful! Thank you for booking with LiftOff.");
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "url('/public/img2.jpg') no-repeat center center/cover",
        padding: "40px",
      }}
    >
      <div
        className="login-card"
        style={{
          width: "1100px", // larger card width
          maxHeight: "90vh", // taller card
          overflowY: "auto",
          padding: "60px 80px",
          display: "flex",
          flexDirection: "column",
          gap: "50px",
          background: "rgba(255, 255, 255, 0.85)",
          borderRadius: "35px",
          boxShadow: "25px 25px 60px #c5c9d1, -25px -25px 60px #ffffff",
          scrollbarWidth: "none", // hide scrollbar (for Firefox)
          msOverflowStyle: "none", // hide scrollbar (for IE/Edge)
        }}
      >
        {/* Hide scrollbar for WebKit browsers */}
        <style>
          {`
            .login-card::-webkit-scrollbar {
              display: none;
            }
          `}
        </style>

        {/* PASSENGER INFO SECTION */}
        <section>
          <div className="login-header">
            <h2 style={{ fontSize: "1.8rem", marginBottom: "15px", textAlign: "center" }}>
              Passenger Information
            </h2>
            <p style={{ textAlign: "center" }}>Please enter passenger details below.</p>
          </div>

          {/* Full Name */}
          <div className="form-group">
            <label
              htmlFor="fullName"
              style={{
                display: "block",
                //marginBottom: "1px",
                color: "#3d4468",
                fontWeight: "600",
              }}
            >
              Full Name
            </label>
            <div className="neu-input">
              <input
                type="text"
                id="fullName"
                placeholder=" "
                value={fullName}
                onChange={(e) => setFullName(e.target.value)}
              />
              <label htmlFor="fullName">Full Name</label>
            </div>
          </div>

          {/* Age */}
          <div className="form-group">
            <label
              htmlFor="age"
              style={{
                display: "block",
                marginBottom: "8px",
                color: "#3d4468",
                fontWeight: "600",
              }}
            >
              Age
            </label>
            <div className="neu-input">
              <input
                type="number"
                id="age"
                placeholder=" "
                min="0"
                max="120"
                value={age}
                onChange={(e) => setAge(e.target.value)}
              />
              <label htmlFor="age">Age</label>
            </div>
          </div>

          {/* Email */}
          <div className="form-group">
            <label
              htmlFor="email"
              style={{
                display: "block",
                marginBottom: "8px",
                color: "#3d4468",
                fontWeight: "600",
              }}
            >
              Email Address
            </label>
            <div className="neu-input">
              <input
                type="email"
                id="email"
                placeholder=" "
                value={email}
                onChange={(e) => setEmail(e.target.value)}
