            <p
              style={{
                fontWeight: "700",
                fontSize: "18px",
                color: "#3d4468",
                display: "flex",
                justifyContent: "space-between",
              }}
            >
              <span>Total</span>
              <span>$108.99</span>
            </p>
          </div>

          <button
            type="submit"
            className="neu-button"
            onClick={handleSubmit}
            style={{ marginTop: "30px" }}
          >
            <span className="btn-text">Confirm & Pay</span>
          </button>

          {message && (
            <p
              style={{
                textAlign: "center",
                color: "#3d4468",
                marginTop: "12px",
                fontWeight: "500",
              }}
            >
              {message}
            </p>
          )}
        </section>

      </div>
    </div>
  );
};

export default PaymentPage;
