import { useState } from "react";

export default function AddFlight() {
  const [flight, setFlight] = useState({
    airline: "",
    flightNo: "",
    from: "",
    to: "",
    depart: "",
    arrive: "",
    durationMin: "",
    stops: "",
    price: "",
    rating: "",

    // Airport info
    fromFull: "",
    fromFullAddress: "",
    terminalFrom: "",
    gateFrom: "",
    toFull: "",
    toFullAddress: "",
    terminalTo: "",
    gateTo: "",
    baggageClaim: "",

    // Local Times
    departureTimeLocal: "",
    arrivalTimeLocal: "",
    timezoneChange: "",

    // Baggage
    carryOnBagsAllowed: "",
    carryOnWeightLimitKg: "",
    personalItemAllowed: "",
    checkedBagsAllowed: "",
    checkedBagWeightLimitKg: "",
    extraBagFeeUSD: "",

    // Aircraft
    aircraftModel: "",
    aircraftCode: "",
    aircraftAgeYears: "",
    aircraftCapacity: "",
    seatLayout: "",
    seatPitch: "",
    seatWidth: "",
    hasUSBOutlets: "",
    hasPowerOutlets: "",
    hasLiveTV: "",

    // Performance
    onTimePercentage: "",
    averageDelayMin: "",
    cancellationRate: "",
    weatherDelayRiskScore: "",
    safetyScore: "",
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFlight({ ...flight, [name]: value });
    if (errors[name]) setErrors({ ...errors, [name]: "" });
  };

  const validateForm = () => {
    const newErrors = {};

    // Check required fields
    Object.keys(flight).forEach((key) => {
      if (!flight[key] || flight[key].toString().trim() === "") {
        newErrors[key] = "Required";
      }
    });

    // Airport codes: 3 uppercase letters
    if (flight.from && !/^[A-Z]{3}$/.test(flight.from.toUpperCase())) {
      newErrors.from = "3 letter code (JFK)";
    }
    if (flight.to && !/^[A-Z]{3}$/.test(flight.to.toUpperCase())) {
      newErrors.to = "3 letter code (LAX)";
    }

    // Numeric validations
    const numericFields = {
      durationMin: "minutes",
      stops: "stops",
      price: "price",
      rating: "rating (1-5)",
      carryOnBagsAllowed: "bags",
      carryOnWeightLimitKg: "weight (kg)",
      checkedBagsAllowed: "bags",
      checkedBagWeightLimitKg: "weight (kg)",
      extraBagFeeUSD: "fee",
      aircraftAgeYears: "years",
      aircraftCapacity: "capacity",
      seatPitch: "inches",
      seatWidth: "inches",
      onTimePercentage: "0-100%",
      averageDelayMin: "minutes",
      cancellationRate: "0-100%",
      weatherDelayRiskScore: "1-5",
      safetyScore: "1-5",
    };

    Object.entries(numericFields).forEach(([field, label]) => {
      if (flight[field] && isNaN(flight[field])) {
        newErrors[field] = `Must be number`;
      }
    });

    // Range validations
    if (flight.rating && (parseFloat(flight.rating) < 1 || parseFloat(flight.rating) > 5)) {
      newErrors.rating = "1-5";
    }
    if (flight.onTimePercentage && (parseFloat(flight.onTimePercentage) < 0 || parseFloat(flight.onTimePercentage) > 100)) {
      newErrors.onTimePercentage = "0-100";
    }
    if (flight.cancellationRate && (parseFloat(flight.cancellationRate) < 0 || parseFloat(flight.cancellationRate) > 100)) {
      newErrors.cancellationRate = "0-100";
    }
    if (flight.weatherDelayRiskScore && (parseFloat(flight.weatherDelayRiskScore) < 1 || parseFloat(flight.weatherDelayRiskScore) > 5)) {
      newErrors.weatherDelayRiskScore = "1-5";
    }
    if (flight.safetyScore && (parseFloat(flight.safetyScore) < 1 || parseFloat(flight.safetyScore) > 5)) {
      newErrors.safetyScore = "1-5";
    }
    if (flight.price && parseFloat(flight.price) < 0) {
      newErrors.price = "Positive #";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      console.log("Submitting Flight:", flight);
      alert("Flight Added Successfully!");
    }
  };

  return (
    <div style={styles.page}>
      <main style={styles.hero}>
        <div style={styles.heroInner}>

          <h2 style={styles.title}>Add New Flight</h2>

          {/* -------------------------------- */}
          {/* 1 SUMMARY CARD */}
          {/* -------------------------------- */}
          <div style={styles.summaryCard}>
            <div style={styles.summaryRow}>

              {/* Airline / Flight No / Rating */}
              <div>
                <label style={styles.label}>Airline</label>
                <input 
                  name="airline" 
                  placeholder="Delta Airlines"
                  value={flight.airline} 
                  onChange={handleChange} 
                  style={styles.input} 
                />

                <label style={styles.label}>Flight Number</label>
                <input 
                  name="flightNo"
                  placeholder="DL245"
                  value={flight.flightNo}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Rating </label>
                <input 
                  name="rating"
                  placeholder="4.6"
                  value={flight.rating}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* From / Depart */}
              <div>
                <label style={styles.label}>From (Airport Code)</label>
                <input 
                  name="from"
                  placeholder="JFK"
                  value={flight.from}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Departure Time</label>
                <input 
                  name="depart"
                  placeholder="08:30 AM"
                  value={flight.depart}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* Duration / Stops */}
              <div>
                <label style={styles.label}>Duration (min)</label>
                <input 
                  name="durationMin"
                  placeholder="180"
                  value={flight.durationMin}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Stops</label>
                <input 
                  name="stops"
                  placeholder="0"
                  value={flight.stops}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* To / Arrive */}
              <div>
                <label style={styles.label}>To (Airport Code)</label>
                <input 
                  name="to"
                  placeholder="LAX"
                  value={flight.to}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Arrival Time</label>
                <input 
                  name="arrive"
                  placeholder="11:15 AM"
                  value={flight.arrive}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* Price */}
              <div>
                <label style={styles.label}>Price ($)</label>
                <input 
                  name="price"
                  placeholder="299"
                  value={flight.price}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

            </div>
          </div>

          {/* -------------------------------- */}
          {/* 2 AIRPORT & ROUTE INFO */}
          {/* -------------------------------- */}
          <div style={styles.sectionCard}>
            <h3>Airport & Route Information</h3>

            <div style={styles.twoColumn}>
              <div>
                <h4 style={styles.subheading}>Departure Airport</h4>

                <label style={styles.label}>Full Name</label>
                <input 
                  name="fromFull"
                  placeholder="John F. Kennedy International Airport"
                  value={flight.fromFull}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Address</label>
                <input 
                  name="fromFullAddress"
                  placeholder="123 Airport Rd, New York, NY"
                  value={flight.fromFullAddress}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Terminal</label>
                <input 
                  name="terminalFrom"
                  placeholder="4"
                  value={flight.terminalFrom}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Gate</label>
                <input 
                  name="gateFrom"
                  placeholder="B22"
                  value={flight.gateFrom}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* Arrival */}
              <div>
                <h4 style={styles.subheading}>Arrival Airport</h4>

                <label style={styles.label}>Full Name</label>
                <input 
                  name="toFull"
                  placeholder="Los Angeles International Airport"
                  value={flight.toFull}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Address</label>
                <input 
                  name="toFullAddress"
                  placeholder="1 World Way, Los Angeles, CA"
                  value={flight.toFullAddress}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Terminal</label>
                <input 
                  name="terminalTo"
                  placeholder="5"
                  value={flight.terminalTo}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Gate</label>
                <input 
                  name="gateTo"
                  placeholder="C19"
                  value={flight.gateTo}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Baggage Claim</label>
                <input 
                  name="baggageClaim"
                  placeholder="Carousel 7"
                  value={flight.baggageClaim}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>
            </div>

            <hr style={styles.separator} />

            <div style={styles.twoColumn}>
              <div>
                <label style={styles.label}>Departure Time (Local)</label>
