import React, { useState } from "react";
import "./Login.css"; // Reuse the same neumorphic styling

const PaymentPage = () => {
  // States
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [age, setAge] = useState("");
  const [cardName, setCardName] = useState("");
  const [cardNumber, setCardNumber] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");
  const [message, setMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false);
  const [isConfirmed, setIsConfirmed] = useState(false);

  // Prices
  const basePrice = 108.99;
  const tax = 8.25;
  const total = (basePrice + tax).toFixed(2);

  // Submit handler
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!fullName || !email || !age || !cardName || !cardNumber || !expiry || !cvv) {
      setMessage("âš ï¸ Please fill in all required fields.");
      return;
    }

    // Start loader
    setIsLoading(true);

    // Simulate 3-second delay before confirmation
    setTimeout(() => {
      setIsLoading(false);
      setIsConfirmed(true);
      setMessage("âœ… Payment successful! Thank you for booking with LiftOff.");
    }, 3000);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        alignItems: "center",
        justifyContent: "center",
        background: "url('/public/img2.jpg') no-repeat center center/cover",
        padding: "40px",
      }}
    >
      <div
        className="login-card"
        style={{
          width: "1100px",
          maxHeight: "90vh",
          overflowY: "auto",
          padding: "60px 80px",
          display: "flex",
          flexDirection: "column",
          gap: "50px",
          background: "rgba(255, 255, 255, 0.85)",
          borderRadius: "35px",
          boxShadow: "25px 25px 60px #c5c9d1, -25px -25px 60px #ffffff",
          scrollbarWidth: "none",
          msOverflowStyle: "none",
        }}
      >
        {/* Hide scrollbar for WebKit browsers */}
        <style>
          {`
            .login-card::-webkit-scrollbar {
              display: none;
            }
            @keyframes spin {
              0% { transform: rotate(0deg); }
              100% { transform: rotate(360deg); }
            }
          `}
        </style>

        {/* ========================= */}
        {/* LOADER & CONFIRMATION LOGIC */}
        {/* ========================= */}
        {isLoading ? (
          // âœ… Loader Screen
          <div style={{ textAlign: "center", padding: "100px 0" }}>
            <div
              style={{
                width: "70px",
                height: "70px",
                border: "6px solid #cfd4db",
                borderTop: "6px solid #28a745",
                borderRadius: "50%",
                margin: "0 auto 20px",
                animation: "spin 1s linear infinite",
              }}
            ></div>
            <h2 style={{ color: "#3d4468" }}>Processing your payment...</h2>
          </div>
        ) : isConfirmed ? (
          // âœ… Confirmation Screen
          <div style={{ textAlign: "center", padding: "100px 20px" }}>
            <h1 style={{ color: "#28a745", fontSize: "2.2rem", marginBottom: "20px" }}>
              ðŸŽ‰ Payment Confirmed!
            </h1>
            <p style={{ color: "#3d4468", fontSize: "1.1rem" }}>
              Thank you, <strong>{fullName}</strong>! Your booking for{" "}
              <strong>Boston â†’ Vermont</strong> is confirmed.
            </p>
            <p style={{ color: "#3d4468", marginTop: "10px" }}>
              A receipt has been sent to <strong>{email}</strong>.
            </p>
          </div>
        ) : (
          // âœ… MAIN PAGE CONTENT
          <>
            {/* PASSENGER INFO SECTION */}
            <section>
              <div className="login-header">
                <h2 style={{ fontSize: "1.8rem", marginBottom: "1px", textAlign: "center" }}>
                  Passenger Information
                </h2>
                <p style={{ textAlign: "center" }}>Please enter passenger details below.</p>
              </div>

              {/* Full Name */}
              <div className="form-group">
                <label
                  htmlFor="fullName"
                  style={{ display: "block", color: "#3d4468", fontWeight: "600" }}
                >
                  Full Name
                </label>
                <div className="neu-input">
                  <input
                    type="text"
                    id="fullName"
                    placeholder=" "
                    value={fullName}
                    onChange={(e) => setFullName(e.target.value)}
                  />
                  <label htmlFor="fullName">Full Name</label>
                </div>
              </div>

              {/* Age */}
              <div className="form-group">
                <label
                  htmlFor="age"
                  style={{
                    display: "block",
                    marginBottom: "8px",
                    color: "#3d4468",
                    fontWeight: "600",
                  }}
                >
                  Age
                </label>
                <div className="neu-input">
                  <input
                    type="number"
                    id="age"
                    placeholder=" "
                    min="0"
                    max="120"
                    value={age}
                    onChange={(e) => setAge(e.target.value)}
                  />
                  <label htmlFor="age">Age</label>
                </div>
              </div>

              {/* Email */}
              <div className="form-group">
                <label
                  htmlFor="email"
                  style={{
                    display: "block",
                    marginBottom: "8px",
                    color: "#3d4468",
                    fontWeight: "600",
                  }}
                >
                  Email Address
                </label>
                <div className="neu-input">
                  <input
                    type="email"
                    id="email"
                    placeholder=" "
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  <label htmlFor="email">Email Address</label>
                </div>
              </div>
            </section>

            {/* PAYMENT INFO SECTION */}
            <section>
              <div className="login-header">
                <h2 style={{ fontSize: "1.8rem", textAlign: "center" }}>
                  Payment Information
                </h2>
                <p style={{ textAlign: "center" }}>Enter your payment details below.</p>
              </div>

              {/* Card Details */}
              <div className="form-group">
                <label
                  htmlFor="cardName"
                  style={{
                    display: "block",
                    marginBottom: "8px",
                    color: "#3d4468",
                    fontWeight: "600",
                  }}
                >
                  Name on Card
                </label>
                <div className="neu-input">
                  <input
                    type="text"
                    id="cardName"
                    placeholder=" "
                    value={cardName}
                    onChange={(e) => setCardName(e.target.value)}
                  />
                  <label htmlFor="cardName">Name on Card</label>
                </div>
              </div>

              <div className="form-group">
                <label
                  htmlFor="cardNumber"
                  style={{
                    display: "block",
                    marginBottom: "8px",
                    color: "#3d4468",
                    fontWeight: "600",
                  }}
                >
                  Card Number
                </label>
                <div className="neu-input">
                  <input
                    type="text"
                    id="cardNumber"
                    placeholder=" "
                    maxLength="19"
                    value={cardNumber}
                    onChange={(e) => setCardNumber(e.target.value)}
                  />
                  <label htmlFor="cardNumber">Card Number</label>
                </div>
              </div>

              <div className="form-group" style={{ display: "flex", gap: "16px" }}>
                <div style={{ flex: 1 }}>
                  <label
                    htmlFor="expiry"
                    style={{
                      display: "block",
                      marginBottom: "8px",
                      color: "#3d4468",
                      fontWeight: "600",
                    }}
                  >
                    Expiration
                  </label>
                  <div className="neu-input">
                    <input
                      type="text"
                      id="expiry"
                      placeholder=" "
                      maxLength="5"
                      value={expiry}
                      onChange={(e) => setExpiry(e.target.value)}
                    />
                    <label htmlFor="expiry">MM/YY</label>
                  </div>
                </div>

                <div style={{ flex: 1 }}>
                  <label
                    htmlFor="cvv"
                    style={{
                      display: "block",
                      marginBottom: "8px",
                      color: "#3d4468",
                      fontWeight: "600",
                    }}
                  >
                    CVV
                  </label>
                  <div className="neu-input">
                    <input
                      type="password"
                      id="cvv"
                      placeholder=" "
                      maxLength="4"
                      value={cvv}
                      onChange={(e) => setCvv(e.target.value)}
                    />
                    <label htmlFor="cvv">CVV</label>
                  </div>
                </div>
              </div>
            </section>

            {/* FLIGHT INFORMATION */}
            <section>
              <div className="login-header">
                <h2 style={{ fontSize: "1.8rem", textAlign: "center" }}>
                  Flight Information
                </h2>
                <p style={{ textAlign: "center" }}>
                  Review your trip details before confirming.
                </p>
              </div>

              <div
                style={{
                  background: "rgba(255,255,255,0.6)",
                  borderRadius: "20px",
                  padding: "30px",
                  boxShadow: "inset 8px 8px 16px #cfd4db, inset -8px -8px 16px #ffffff",
                }}
              >
                <div
                  style={{
                    display: "flex",
                    justifyContent: "space-between",
                    alignItems: "center",
                    marginBottom: "10px",
                  }}
                >
                  <p
                    style={{
                      fontWeight: "600",
                      fontSize: "1rem",
                      color: "#3d4468",
                      margin: 0,
                    }}
                  >
                    Boston (BSTN) â†’ Vermont (VRMNT)
                  </p>
                  <p
                    style={{
                      fontWeight: "600",
                      fontSize: "1rem",
                      color: "#3d4468",
                      margin: 0,
                    }}
                  >
                    ${basePrice}
                  </p>
                </div>

                <p style={{ color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Flight Number: LF234
                </p>
                <p style={{ color: "#6c7293", fontSize: "15px", marginLeft: "25px" }}>
                  Airline: LiftOff Airlines
                </p>
                <p style={{ color: "#6c7293", fontSize: "15px", marginLeft: "25px" }}>
                  Departure: Nov 10, 2025 - 8:45 AM
                </p>
                <p style={{ color: "#6c7293", fontSize: "15px", marginLeft: "25px" }}>
                  Number of Passengers: 1
                </p>
                <p style={{ color: "#6c7293", fontSize: "15px", marginLeft: "25px" }}>
                  Seat Number: 12A
                </p>
              </div>
            </section>

            {/* COMPLETE SUMMARY */}
            <section>
              <div className="login-header">
                <h2 style={{ fontSize: "1.8rem", textAlign: "center", marginBottom: "10px" }}>
                  Complete Summary
                </h2>
                <p style={{ textAlign: "center" }}>
                  Please review your booking information before final confirmation.
                </p>
              </div>

              <div
                style={{
                  background: "rgba(255,255,255,0.6)",
                  borderRadius: "20px",
                  padding: "35px",
                  boxShadow: "inset 8px 8px 16px #cfd4db, inset -8px -8px 16px #ffffff",
                }}
              >
                <h3 style={{ color: "#3d4468", fontSize: "1.4rem" }}>Passenger</h3>
                <p style={{color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Name: {fullName || "â€”"}
                </p>
                <p style={{color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Age: {age || "â€”"}
                </p>
                <p style={{color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Seat Number: 12A
                </p>

                <div
                  style={{
                    height: "2px",
                    background: "linear-gradient(90deg, transparent, #bec3cf, transparent)",
                    margin: "20px 0",
                  }}
                ></div>

                <h3 style={{ color: "#3d4468", fontSize: "1.4rem" }}>Flight</h3>
                <p style={{color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Flight Number: LF234
                </p>
                <p style={{color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Airline: LiftOff Airlines
                </p>
                <p style={{color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Route: Boston (BSTN) â†’ Vermont (VRMNT)
                </p>
                <p style={{color: "#7f8087ff", fontWeight: "600", marginLeft: "25px",  marginBottom: "4px"}}>
                  Departure: Nov 10, 2025 - 8:45 AM
                </p>

                <div
                  style={{
                    height: "2px",
                    background: "linear-gradient(90deg, transparent, #bec3cf, transparent)",
                    margin: "20px 0",
                  }}
                ></div>

                <h3 style={{ color: "#3d4468", fontSize: "1.4rem" }}>Amount to Pay</h3>
                <p style={{ color: "#3d4468", fontWeight: "600", marginLeft: "25px" }}>
                  Base Fare: ${basePrice} <br />
                  Tax: ${tax} <br />
                  <strong>Total: ${total}</strong>
                </p>

                <div
                  style={{
                    height: "2px",
                    background: "linear-gradient(90deg, transparent, #bec3cf, transparent)",
                    margin: "20px 0",
                  }}
                ></div>

                {/* Email Receipt at Bottom */}
                <p
                  style={{
                    color: "#3d4468",
                    fontWeight: "600",
                    textAlign: "center",
                  }}
                >
                  Email Receipt to: {email || "â€”"}
                </p>
              </div>

              {/* Confirmation Checkboxes */}
              <div style={{ marginTop: "25px" }}>
                <label
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "10px",
                    color: "#3d4468",
                    fontSize: "15px",
                    marginBottom: "10px",
                  }}
                >
                  <input type="checkbox" required />
                  I have read and confirm the information above and allow you to charge my credit card.
                </label>

                <label
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "10px",
                    color: "#3d4468",
                    fontSize: "15px",
                  }}
                >
                  <input type="checkbox" required />
                  I have read and agree to the terms and conditions.
                </label>
              </div>

              {/* âœ… Green Submit Button */}
              <button
                type="submit"
                onClick={handleSubmit}
                style={{
                  marginTop: "35px",
                  width: "100%",
                  background: "#28a745",
                  border: "none",
                  borderRadius: "15px",
                  padding: "18px 32px",
                  color: "white",
                  fontSize: "16px",
                  fontWeight: "600",
                  cursor: "pointer",
                  transition: "all 0.3s ease",
                  boxShadow: "0 4px 15px rgba(0,0,0,0.2)",
                }}
                onMouseOver={(e) => (e.target.style.background = "#23963e")}
                onMouseOut={(e) => (e.target.style.background = "#28a745")}
              >
                Submit & Finalize
              </button>

              {message && (
                <p
                  style={{
                    textAlign: "center",
                    color: "#3d4468",
                    marginTop: "12px",
                    fontWeight: "500",
                  }}
                >
                  {message}
                </p>
              )}
            </section>
          </>
        )}
      </div>
    </div>
  );
};

export default PaymentPage;
