import React, { useState } from "react";
import "./Support.css";

function Support() {
  const [openIndex, setOpenIndex] = useState(null);

  const faqs = [
    {
      question: "How can I change my flight date?",
      answer:
        "You can change your flight date by visiting the 'My Flights' tab and selecting the flight you wish to modify.",
    },
    {
      question: "How do I earn reward points?",
      answer:
        "You earn points for every completed flight. The amount depends on your ticket class and distance flown.",
    },
    {
      question: "Can I check in without my confirmation code?",
      answer:
        "Yes, you can use your last name and frequent flyer number to check in if you’ve lost your confirmation code.",
    },
    {
      question: "How do I contact customer service?",
      answer:
        "We’re available 24/7 via this form or by calling +1 (518) 555-0123.",
    },
  ];

  return (
    <div className="user-page support">
      {/* ===== HEADER ===== */}
      <h2 className="section-title">Customer Support</h2>
      <p className="support-text">
        Need help? Browse common questions or submit a support ticket.
      </p>

      {/* ===== SIDE-BY-SIDE LAYOUT ===== */}
      <div className="support-layout">
        {/* --- LEFT SIDE: FAQ --- */}
        <div className="faq-section">
          <h3 className="faq-title">Frequently Asked Questions</h3>
          <div className="faq-list">
            {faqs.map((faq, index) => (
              <div
                key={index}
                className={`faq-item ${openIndex === index ? "open" : ""}`}
                onClick={() =>
                  setOpenIndex(openIndex === index ? null : index)
                }
              >
                <div className="faq-question">
                  <span>{faq.question}</span>
                  <span className="faq-icon">
                    {openIndex === index ? "−" : "+"}
                  </span>
                </div>
                {openIndex === index && (
                  <div className="faq-answer">{faq.answer}</div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* --- RIGHT SIDE: SUPPORT FORM --- */}
        <div className="support-form-container">
          <form className="support-form" onSubmit={async (e) => {
              e.preventDefault();
              setStatus('loading');
              try {
                const payload = { name, email, subject, message };
                // include userId if user is logged in
                const stored = localStorage.getItem('liftoffUser');
                if (stored) {
                  const parsed = JSON.parse(stored);
                  if (parsed && parsed._id) payload.userId = parsed._id;
                }

                const res = await fetch('http://localhost:9000/ticket', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify(payload)
                });

                if (!res.ok) throw new Error('Failed to submit ticket');
                const data = await res.json();
                setStatus('success');
                setName(''); setEmail(''); setSubject(''); setMessage('');
              } catch (err) {
                console.error(err);
                setStatus('error');
              }
            }}>
            <h3 className="form-title">Submit a Ticket</h3>
            <p className="support-text">
              Need help? Browse common questions or submit a support ticket.
            </p>

            <input value={name} onChange={e => setName(e.target.value)} type="text" placeholder="Your Name" required />
            <input value={email} onChange={e => setEmail(e.target.value)} type="email" placeholder="Your Email" required /> {/* Added email field */}
            <input value={subject} onChange={e => setSubject(e.target.value)} type="text" placeholder="Subject" required />
            <textarea value={message} onChange={e => setMessage(e.target.value)} placeholder="Describe your issue..." rows="4" required />

            <button type="submit">Submit Ticket</button>

            {status === 'loading' && <div className="support-note">Submitting...</div>}
            {status === 'success' && <div className="support-note success">Ticket submitted — we'll reply via email.</div>}
            {status === 'error' && <div className="support-note error">Failed to submit. Try again later.</div>}
          </form>
        </div>
      </div>
    </div>
  );
}

export default Support;
