import React, { useState, useMemo, useEffect } from "react";
import axios from "axios";
import "./UserDashboard.css";


export default function UserDashboard() {
  const [tab, setTab] = useState("info");

  return (
    <div className="user-page">
      <div className="user-header">
        <h1>Your Profile</h1>
        <p>Manage your profile, flights, and rewards</p>
      </div>

      <div className="user-tabs">
        {["info", "flights", "points", "checkin"].map((name) => (
          <button
            key={name}
            onClick={() => setTab(name)}
            className={`tab-btn ${tab === name ? "active" : ""}`}
          >
            {name === "info" && "My Info"}
            {name === "flights" && "My Flights"}
            {name === "points" && "My Points"}
            {name === "checkin" && "Check-In"}
          </button>
        ))}
      </div>

      <div className="user-content">
        {tab === "info" && <ProfileInfo />}
        {tab === "flights" && <FlightHistory />}
        {tab === "points" && <Points />}
        {tab === "checkin" && <CheckIn />}
      </div>
    </div>
  );
}

// ==========================================================

function ProfileInfo() {
  const user = JSON.parse(localStorage.getItem("liftoffUser"));

  const [isEditingCard, setIsEditingCard] = useState(false);
  const [cardInfo, setCardInfo] = useState(() => {
    const saved = localStorage.getItem("userCardInfo");
    return saved ? JSON.parse(saved) : { cardNumber: "", cardName: "", expiry: "", cvv: "" };
  });
  const [tempCardInfo, setTempCardInfo] = useState(cardInfo);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  const handleSaveCard = () => {
    if (!tempCardInfo.cardNumber || !tempCardInfo.cardName || !tempCardInfo.expiry || !tempCardInfo.cvv) {
      alert("Please fill in all card fields.");
      return;
    }

    if (tempCardInfo.cardNumber.length !== 16 || !/^\d+$/.test(tempCardInfo.cardNumber)) {
      alert("Card number must be 16 digits.");
      return;
    }

    if (!/^\d{2}\/\d{2}$/.test(tempCardInfo.expiry)) {
      alert("Expiry must be in MM/YY format.");
      return;
    }

    if (!/^\d{3,4}$/.test(tempCardInfo.cvv)) {
      alert("CVV must be 3 or 4 digits.");
      return;
    }

    setCardInfo(tempCardInfo);
    localStorage.setItem("userCardInfo", JSON.stringify(tempCardInfo));
    setIsEditingCard(false);
  };

  const handleCancelCard = () => {
    setTempCardInfo(cardInfo);
    setIsEditingCard(false);
  };

  const handleDeleteCard = () => {
    setShowDeleteConfirm(true);
  };

  const confirmDeleteCard = () => {
    const emptyCard = { cardNumber: "", cardName: "", expiry: "", cvv: "" };
    setCardInfo(emptyCard);
    setTempCardInfo(emptyCard);
    localStorage.removeItem("userCardInfo");
    setShowDeleteConfirm(false);
  };

  const cancelDeleteCard = () => {
    setShowDeleteConfirm(false);
  };

  const maskCardNumber = (cardNum) => {
    if (!cardNum) return "";
    return "**** **** **** " + cardNum.slice(-4);
  };

  return (
    <div className="profile-card">
      <p>
        <strong className="highlight">Name:</strong> 
        {user ? ` ${user.firstName} ${user.lastName}` : " Not logged in"}
      </p>

      <p>
        <strong className="highlight">Email:</strong> 
        {user ? ` ${user.username}` : " Not logged in"}
      </p>

      <p>
        <strong className="highlight">Frequent Flyer #:</strong> 
        {user ? ` LIFT-${user._id.slice(-5).toUpperCase()}` : "N/A"} 
      </p>

      <p>
        <strong className="highlight">Upcoming Flight:</strong>  
        You currently have no upcoming flights.
      </p>

      <div style={{ marginTop: "24px", paddingTop: "24px", borderTop: "2px solid #f0f0f0" }}>
        <p style={{ margin: "0 0 16px 0", fontSize: "18px", fontWeight: 700 }}>
          <strong className="highlight">Card Info</strong>
        </p>

        {isEditingCard ? (
          <div style={{ display: "grid", gap: "12px" }}>
            <div>
              <label style={{ display: "block", fontSize: "12px", marginBottom: "4px", color: "#666" }}>
                Card Number (16 digits)
              </label>
              <input
                type="text"
                maxLength="16"
                placeholder="1234567890123456"
                value={tempCardInfo.cardNumber}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, "");
                  setTempCardInfo({ ...tempCardInfo, cardNumber: value });
                }}
                style={{
                  width: "100%",
                  padding: "8px 12px",
                  border: "1px solid #cfd7df",
                  borderRadius: "6px",
                  fontSize: "14px",
                  boxSizing: "border-box"
                }}
              />
            </div>

            <div>
              <label style={{ display: "block", fontSize: "12px", marginBottom: "4px", color: "#666" }}>
                Cardholder Name
              </label>
              <input
                type="text"
                placeholder="John Doe"
                value={tempCardInfo.cardName}
                onChange={(e) => setTempCardInfo({ ...tempCardInfo, cardName: e.target.value })}
                style={{
                  width: "100%",
                  padding: "8px 12px",
                  border: "1px solid #cfd7df",
                  borderRadius: "6px",
                  fontSize: "14px",
                  boxSizing: "border-box"
                }}
              />
            </div>

            <div>
              <label style={{ display: "block", fontSize: "12px", marginBottom: "4px", color: "#666" }}>
                Expiry (MM/YY)
              </label>
              <input
                type="text"
                maxLength="5"
                placeholder="12/25"
                value={tempCardInfo.expiry}
                onChange={(e) => {
                  let value = e.target.value.replace(/\D/g, "");
                  if (value.length >= 2) {
                    value = value.slice(0, 2) + "/" + value.slice(2, 4);
                  }
                  setTempCardInfo({ ...tempCardInfo, expiry: value });
                }}
                style={{
                  width: "100%",
                  padding: "8px 12px",
                  border: "1px solid #cfd7df",
                  borderRadius: "6px",
                  fontSize: "14px",
                  boxSizing: "border-box"
                }}
              />
            </div>

            <div>
              <label style={{ display: "block", fontSize: "12px", marginBottom: "4px", color: "#666" }}>
                CVV (3-4 digits)
              </label>
              <input
                type="password"
                maxLength="4"
                placeholder="123"
                value={tempCardInfo.cvv}
                onChange={(e) => {
                  const value = e.target.value.replace(/\D/g, "");
                  setTempCardInfo({ ...tempCardInfo, cvv: value });
                }}
                style={{
                  width: "100%",
                  padding: "8px 12px",
                  border: "1px solid #cfd7df",
                  borderRadius: "6px",
                  fontSize: "14px",
                  boxSizing: "border-box"
                }}
              />
            </div>

            <div style={{ display: "flex", gap: "8px", marginTop: "8px" }}>
              <button
                onClick={handleSaveCard}
                style={{
                  flex: 1,
                  padding: "8px 12px",
                  background: "#28a745",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  fontWeight: 600,
                  fontSize: "14px"
                }}
              >
                Save Card
              </button>
              <button
                onClick={handleCancelCard}
                style={{
                  flex: 1,
                  padding: "8px 12px",
                  background: "#6c757d",
                  color: "white",
                  border: "none",
                  borderRadius: "6px",
                  cursor: "pointer",
                  fontWeight: 600,
                  fontSize: "14px"
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        ) : (
          <>
            {cardInfo.cardNumber ? (
              <div style={{
                background: "#f8f9fa",
                padding: "16px",
                borderRadius: "8px",
                border: "1px solid #e7ebf0"
              }}>
                <p style={{ margin: "0 0 8px 0", fontSize: "14px", color: "#666" }}>
                  <strong>Card Number:</strong> {maskCardNumber(cardInfo.cardNumber)}
                </p>
                <p style={{ margin: "0 0 8px 0", fontSize: "14px", color: "#666" }}>
                  <strong>Cardholder:</strong> {cardInfo.cardName}
                </p>
                <p style={{ margin: "0 0 16px 0", fontSize: "14px", color: "#666" }}>
                  <strong>Expires:</strong> {cardInfo.expiry}
                </p>
                <div style={{ display: "flex", gap: "8px" }}>
                  <button
                    onClick={() => setIsEditingCard(true)}
                    style={{
                      flex: 1,
                      padding: "8px 12px",
                      background: "#2f6feb",
                      color: "white",
                      border: "none",
                      borderRadius: "6px",
                      cursor: "pointer",
                      fontWeight: 600,
                      fontSize: "14px"
                    }}
                  >
                    Edit
                  </button>
                  <button
                    onClick={handleDeleteCard}
                    style={{
                      flex: 1,
                      padding: "8px 12px",
                      background: "#dc3545",
                      color: "white",
                      border: "none",
                      borderRadius: "6px",
                      cursor: "pointer",
                      fontWeight: 600,
                      fontSize: "14px"
                    }}
                  >
                    Delete
                  </button>
                </div>
              </div>
            ) : (
              <div style={{
                background: "#f8f9fa",
                padding: "16px",
                borderRadius: "8px",
                border: "1px solid #e7ebf0",
                textAlign: "center"
              }}>
                <p style={{ margin: "0 0 12px 0", fontSize: "14px", color: "#666" }}>
                  No card saved yet.
                </p>
                <button
                  onClick={() => setIsEditingCard(true)}
                  style={{
                    padding: "8px 16px",
                    background: "#2f6feb",
                    color: "white",
                    border: "none",
                    borderRadius: "6px",
                    cursor: "pointer",
                    fontWeight: 600,
                    fontSize: "14px"
                  }}
                >
                  Add Card
                </button>
              </div>
            )}
          </>
        )}
      </div>

      {showDeleteConfirm && (
        <>
          <div style={{
            position: "fixed",
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            background: "rgba(0, 0, 0, 0.5)",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            zIndex: 1000
          }}>
            <div style={{
              background: "white",
              borderRadius: "12px",
              padding: "24px",
              maxWidth: "400px",
              width: "90%",
              boxShadow: "0 4px 20px rgba(0, 0, 0, 0.15)",
              textAlign: "center"
            }}>
              <h3 style={{ margin: "0 0 12px 0", fontSize: "18px", fontWeight: 700, color: "#111" }}>
                Delete Card?
              </h3>

              <p style={{ margin: "0 0 24px 0", fontSize: "14px", color: "#666", lineHeight: "1.5" }}>
                Are you sure you want to delete this card? This action cannot be undone.
              </p>

              <div style={{ display: "flex", gap: "12px" }}>
                <button
                  onClick={cancelDeleteCard}
                  style={{
                    flex: 1,
                    padding: "10px 16px",
                    background: "#e9ecef",
                    color: "#333",
                    border: "none",
                    borderRadius: "6px",
                    cursor: "pointer",
                    fontWeight: 600,
                    fontSize: "14px",
                    transition: "background 0.2s"
                  }}
                  onMouseOver={(e) => e.target.style.background = "#dee2e6"}
                  onMouseOut={(e) => e.target.style.background = "#e9ecef"}
                >
                  Cancel
                </button>

                <button
                  onClick={confirmDeleteCard}
                  style={{
                    flex: 1,
                    padding: "10px 16px",
                    background: "#dc3545",
                    color: "white",
                    border: "none",
                    borderRadius: "6px",
                    cursor: "pointer",
                    fontWeight: 600,
                    fontSize: "14px",
                    transition: "background 0.2s"
                  }}
                  onMouseOver={(e) => e.target.style.background = "#c82333"}
                  onMouseOut={(e) => e.target.style.background = "#dc3545"}
                >
                  Delete Card
                </button>
              </div>
            </div>
          </div>
        </>
      )}
    </div>
  );
}


