import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css"; 
import axios from 'axios';

const SignUpPage = () => {
  const navigate = useNavigate();

  const [message, setMessage] = useState("");
  const [messageColor, setMessageColor] = useState("");

  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const [showPassword, setShowPassword] = useState(false);
  const togglePassword = () => setShowPassword(!showPassword);

  const [showSuccess, setShowSuccess] = useState(false); // ⭐ SUCCESS POPUP

  // HANDLE SIGNUP
  const handleSignUp = (event, firstName, lastName, username, password) => { 
    event.preventDefault();

    // Validation
    if (!firstName.trim() || !email.trim() || !password.trim()) {
      setMessage("Please fill out all required fields.");
      setMessageColor("red");
      return;
    }

    axios.post("http://localhost:9000/createUser", {
      firstName,
      lastName,
      username,
      password
    })
      .then((res) => {
        // Clear inline message
        setMessage("");

        // Show success popup
        setShowSuccess(true);

        // Redirect to Login after delay
        setTimeout(() => {
          setShowSuccess(false);
          navigate("/Login");
        }, 1500);
      })
      .catch(() => {
        setMessage("Error during signup. Try again.");
        setMessageColor("red");
      });
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h2 className="logo-text"><span>Liftoff</span></h2>
          <p className="info-login">Create your account</p>
        </div>

        <form className="login-form" onSubmit={(e) => e.preventDefault()} noValidate>

          {/* FIRST NAME */}
          <div className="form-group">
            <div className="input-group neu-input">
              <input
                type="text"
                placeholder=" "
                value={firstName}
                onChange={(e)=> setFirstName(e.target.value)}
                required
              />
              <label>First Name</label>
            </div>
          </div>

          {/* LAST NAME */}
          <div className="form-group">
            <div className="input-group neu-input">
              <input
                type="text"
                placeholder=" "
                value={lastName}
                onChange={(e)=> setLastName(e.target.value)}
                required
              />
              <label>Last Name</label>
            </div>
          </div>

          {/* EMAIL */}
          <div className="form-group">
            <div className="input-group neu-input">
              <input
                type="email"
                placeholder=" "
                value={email}
                onChange={(e)=> setEmail(e.target.value)}
                required
              />
              <label>Email</label>
            </div>
          </div>

          {/* PASSWORD */}
          <div className="form-group">
            <div className="input-group neu-input password-group">
              <input
                type={showPassword ? "text" : "password"}
                placeholder=" "
                value={password}
                onChange={(e)=> setPassword(e.target.value)}
                required
              />
              <label>Password</label>

              <button
                type="button"
                className={`neu-toggle ${showPassword ? "show-password" : ""}`}
                onClick={togglePassword}
              >
                <svg className="eye-open" viewBox="0 0 24 24">
                  <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z" />
                </svg>

                <svg className="eye-closed" viewBox="0 0 24 24">
                  <line x1="1" y1="1" x2="23" y2="23" />
                </svg>
              </button>
            </div>
          </div>

          {/* INLINE ERROR MESSAGE */}
          {message && (
            <p style={{ color: messageColor, fontWeight:"bold", textAlign:"center" }}>
              {message}
            </p>
          )}

          {/* SIGN UP BUTTON */}
          <button
            type="submit"
            className="neu-button signup-btn"
            onClick={(e) =>
              handleSignUp(e, firstName, lastName, email, password)
            }
          >
            <span className="btn-text">Sign Up</span>
            <div className="btn-loader">
              <div className="neu-spinner"></div>
            </div>
          </button>
        </form>

        <div className="signup-link">
          <button
            type="button"
            className="neu-button login-btn"
            onClick={() => navigate("/Login")}
          >
            Login
          </button>
        </div>
      </div>

      {/* SUCCESS POPUP CARD ⭐⭐⭐ */}
      {showSuccess && (
        <div style={styles.overlay}>
          <div style={styles.successCard}>
            <h3>Signup Successful!</h3>
            <p>Please use your credentials to log in.</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default SignUpPage;

// ⭐ SUCCESS MESSAGE CARD STYLES
const styles = {
  overlay: {
    position: "fixed",
    top: 0, left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0,0,0,0.45)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 9999,
  },
  successCard: {
    background: "white",
    padding: "30px 40px",
    borderRadius: "14px",
    boxShadow: "0 5px 20px rgba(0,0,0,0.2)",
    textAlign: "center",
    fontFamily: "system-ui",
    width: "350px",
  },
};
