import { useParams, useNavigate } from "react-router-dom";
import { useState } from "react";

export default function UpdateFlightStatus() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [status, setStatus] = useState("On-Time");

  const handleSave = () => {
    alert(`Status for Flight ${id} updated to: ${status}`);
    navigate("/seller-flights");
  };

  return (
    <div style={styles.page}>
      <main style={styles.hero}>
        <div style={styles.heroInner}>

          <h2 style={styles.title}>Update Flight Status</h2>

          <div style={styles.sectionCard}>
            <p style={{ fontSize: "17px" }}>
              Update status for <strong>Flight {id}</strong>
            </p>

            <label style={styles.label}>Select New Status</label>

            <select
              value={status}
              onChange={(e) => setStatus(e.target.value)}
              style={styles.select}
            >
              <option value="On-Time">On-Time</option>
              <option value="Delayed">Delayed</option>
              <option value="Cancelled">Cancelled</option>
            </select>

            <button style={styles.saveBtn} onClick={handleSave}>
              Save Status
            </button>

            <button style={styles.backBtn} onClick={() => navigate("/seller-flights")}>
              ‚Üê Back to Flights
            </button>
          </div>

        </div>
      </main>
    </div>
  );
}

const styles = {
  page: {
    fontFamily: "system-ui, sans-serif",
    color: "#111",
  },

  hero: {
    background: "#f6f7fbce",
    padding: "30px 0",
    borderRadius: "20px",
    width: "600px",
    margin: "40px auto",
    boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
  },

  heroInner: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: 20,
  },

  title: {
    fontSize: 26,
    fontWeight: 700,
  },

  sectionCard: {
    width: "500px",
    background: "white",
    border: "1px solid #e7ebf0",
    borderRadius: 12,
    padding: 25,
    boxShadow: "0 1px 3px rgba(0,0,0,.05)",
  },

  label: {
    display: "block",
    marginBottom: 6,
    fontWeight: 600,
    fontSize: 15,
  },

  select: {
    width: "100%",
    padding: "12px",
    borderRadius: "8px",
    border: "1px solid #ccc",
    marginBottom: "18px",
    fontSize: "15px",
  },

  saveBtn: {
    width: "100%",
    padding: "14px",
    background: "linear-gradient(135deg, #2f6feb, #1b4fba)",
    color: "white",
    borderRadius: "10px",
    border: "none",
    fontSize: "16px",
    fontWeight: 700,
    cursor: "pointer",
    marginBottom: "10px",
  },

  backBtn: {
    width: "100%",
    padding: "12px",
    background: "#ddd",
    borderRadius: "8px",
    border: "none",
    fontSize: "15px",
    cursor: "pointer",
  },
};
