// Homepage.jsx (or homepage.js)
import { useMemo, useState } from "react";
import { Link } from "react-router-dom";


export default function Homepage() {
  // ---- Query state ----
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [date, setDate] = useState("");
  const [passengers, setPassengers] = useState(1);
  const [airline, setAirline] = useState("All");
  const [nonstopOnly, setNonstopOnly] = useState(false);
  const [sortBy, setSortBy] = useState("priceAsc");

  // ---- Dummy flight data (client-side) ----
  const flights = useMemo(
    () => [
      // id, airline, flightNo, from, to, depart (HH:mm), arrive (HH:mm), durationMin, price, rating, stops(0/1+), date
      { id: 1, airline: "Delta", flightNo: "DL 1234", from: "JFK", to: "LHR", depart: "08:30", arrive: "20:45", durationMin: 435, price: 899, rating: 4.5, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: true, entertainment: true } },
      { id: 2, airline: "United", flightNo: "UA 22", from: "EWR", to: "SFO", depart: "07:10", arrive: "10:35", durationMin: 325, price: 279, rating: 4.1, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: false, entertainment: true } },
      { id: 3, airline: "American", flightNo: "AA 90", from: "JFK", to: "MIA", depart: "12:15", arrive: "15:20", durationMin: 185, price: 189, rating: 4.0, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: false, entertainment: true } },
      { id: 4, airline: "JetBlue", flightNo: "B6 777", from: "JFK", to: "LAX", depart: "16:00", arrive: "19:30", durationMin: 330, price: 315, rating: 4.3, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: false, entertainment: true } },
      { id: 5, airline: "British Airways", flightNo: "BA 178", from: "JFK", to: "LHR", depart: "21:00", arrive: "09:10", durationMin: 430, price: 945, rating: 4.6, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: true, entertainment: true } },
      { id: 6, airline: "Southwest", flightNo: "WN 330", from: "DAL", to: "DEN", depart: "09:20", arrive: "10:55", durationMin: 95, price: 129, rating: 4.2, stops: 0, date: "2025-11-21", amenities: { wifi: true, meals: false, entertainment: false } },
      { id: 7, airline: "United", flightNo: "UA 790", from: "ALB", to: "DFW", depart: "06:50", arrive: "12:10", durationMin: 320, price: 258, rating: 4.0, stops: 1, date: "2025-11-20", amenities: { wifi: true, meals: false, entertainment: true } },
      { id: 8, airline: "American", flightNo: "AA 14", from: "LGA", to: "DFW", depart: "13:10", arrive: "16:00", durationMin: 170, price: 235, rating: 4.1, stops: 0, date: "2025-11-21", amenities: { wifi: true, meals: false, entertainment: true } },
      { id: 9, airline: "Delta", flightNo: "DL 88", from: "JFK", to: "CDG", depart: "19:25", arrive: "08:55", durationMin: 510, price: 740, rating: 4.4, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: true, entertainment: true } },
      { id: 10, airline: "JetBlue", flightNo: "B6 901", from: "ALB", to: "JFK", depart: "10:00", arrive: "10:58", durationMin: 58, price: 89, rating: 4.2, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: false, entertainment: false } },
      { id: 11, airline: "Frontier", flightNo: "F9 300", from: "ALB", to: "MCO", depart: "17:45", arrive: "20:40", durationMin: 175, price: 119, rating: 3.6, stops: 0, date: "2025-11-22", amenities: { wifi: false, meals: false, entertainment: false } },
      { id: 12, airline: "United", flightNo: "UA 70", from: "EWR", to: "LHR", depart: "22:10", arrive: "10:05", durationMin: 475, price: 820, rating: 4.2, stops: 0, date: "2025-11-20", amenities: { wifi: true, meals: true, entertainment: true } },
    ],
    []
  );

  // airlines list for dropdown
  const airlineOptions = useMemo(
    () => ["All", ...Array.from(new Set(flights.map(f => f.airline)))],
    [flights]
  );

  // ---- Filtering + sorting ----
  const results = useMemo(() => {
    let out = flights.filter(f => {
      const matchFrom = from ? f.from.toLowerCase().includes(from.trim().toLowerCase()) : true;
      const matchTo = to ? f.to.toLowerCase().includes(to.trim().toLowerCase()) : true;
      const matchDate = date ? f.date === date : true;
      const matchAirline = airline === "All" ? true : f.airline === airline;
      const matchStops = nonstopOnly ? f.stops === 0 : true;
      return matchFrom && matchTo && matchDate && matchAirline && matchStops;
    });

    switch (sortBy) {
      case "priceAsc": out.sort((a, b) => a.price - b.price); break;
      case "priceDesc": out.sort((a, b) => b.price - a.price); break;
      case "durationAsc": out.sort((a, b) => a.durationMin - b.durationMin); break;
      case "departAsc": out.sort((a, b) => toMinutes(a.depart) - toMinutes(b.depart)); break;
      case "ratingDesc": out.sort((a, b) => b.rating - a.rating); break;
      default: break;
    }
    return out;
  }, [flights, from, to, date, airline, nonstopOnly, sortBy]);

  return (
    <div style={styles.page}>
      {/* Top bar */}
      <header style={styles.header}>
        <div style={styles.container}>
          <div style={styles.brand}>LiftOff</div>
          <nav style={styles.nav}>
  <Link style={styles.link} to="/home">Book</Link>
  <Link style={styles.link} to="/check-in">Check-in</Link>
  <Link style={styles.link} to="/profile">Profile</Link>
</nav>

        </div>
      </header>

      {/* Hero + Search */}
      <main style={styles.hero}>
        <div style={{...styles.container, ...styles.heroInner}}>
          <h1 style={styles.title}>Find Your Perfect Flight</h1>

          {/* Search + filters card */}
          <form
            style={styles.card}
            onSubmit={(e) => {
              e.preventDefault();
              // No network call — filters already apply reactively
            }}
          >
            <div style={styles.row}>
              <label style={styles.label}>
                From
                <input
                  style={styles.input}
                  placeholder="e.g., JFK, EWR, ALB…"
                  value={from}
                  onChange={(e) => setFrom(e.target.value)}
                />
              </label>

              <label style={styles.label}>
                To
                <input
                  style={styles.input}
                  placeholder="e.g., LHR, SFO, MIA…"
                  value={to}
                  onChange={(e) => setTo(e.target.value)}
                />
              </label>
            </div>

            <div style={styles.row}>
              <label style={styles.label}>
                Depart
                <input
                  type="date"
                  style={styles.input}
                  value={date}
                  onChange={(e) => setDate(e.target.value)}
                />
              </label>

              <label style={styles.label}>
                Passengers
                <input
                  type="number"
                  min="1"
                  style={styles.input}
                  value={passengers}
                  onChange={(e) => setPassengers(parseInt(e.target.value || "1", 10))}
                />
              </label>
            </div>

            <div style={styles.row}>
              <label style={styles.label}>
                Airline
                <select
                  style={styles.input}
                  value={airline}
                  onChange={(e) => setAirline(e.target.value)}
                >
                  {airlineOptions.map(a => <option key={a} value={a}>{a}</option>)}
                </select>
              </label>

              <label style={{...styles.label, alignItems: "center"}}>
                <span style={{marginBottom: 6}}>Nonstop only</span>
                <input
                  type="checkbox"
                  checked={nonstopOnly}
                  onChange={(e) => setNonstopOnly(e.target.checked)}
                />
              </label>
            </div>

            <div style={styles.row}>
              <label style={styles.label}>
                Sort by
                <select
                  style={styles.input}
                  value={sortBy}
                  onChange={(e) => setSortBy(e.target.value)}
                >
                  <option value="priceAsc">Price (low → high)</option>
                  <option value="priceDesc">Price (high → low)</option>
                  <option value="durationAsc">Duration (shortest)</option>
                  <option value="departAsc">Departure time (earliest)</option>
                  <option value="ratingDesc">Rating (best)</option>
                </select>
              </label>
              <div />
            </div>

            <button style={styles.button} type="submit">Search Flights</button>
          </form>

          {/* Results header */}
          <div style={{display: "flex", justifyContent: "space-between", alignItems: "center"}}>
            <div style={{color:"#666"}}>{results.length} flights found</div>
          </div>

          {/* Results list */}
          <div style={{display: "grid", gap: 12}}>
            {results.map(f => (
              <article key={f.id} style={styles.resultCard}>
                <div style={{display:"grid", gridTemplateColumns:"1.2fr 1fr 1fr 1fr auto", alignItems:"center", gap:12}}>
                  <div>
                    <div style={{fontWeight:700}}>{f.airline} <span style={styles.badge}>{f.flightNo}</span></div>
                    <div style={{fontSize:12, color:"#666"}}>{airportName(f.from)} → {airportName(f.to)}</div>
                  </div>

                  <div>
                    <div style={styles.time}>{f.depart}</div>
                    <div style={{fontSize:12, color:"#666"}}>{cityFromCode(f.from)}</div>
                  </div>

                  <div style={{textAlign:"center"}}>
                    <div style={{fontSize:12, color:"#666"}}>{formatDuration(f.durationMin)}</div>
                    <div style={{fontSize:12}}>{f.stops === 0 ? "Nonstop" : `${f.stops} stop`}</div>
                  </div>

                  <div style={{textAlign:"right"}}>
                    <div style={styles.time}>{f.arrive}</div>
                    <div style={{fontSize:12, color:"#666"}}>{cityFromCode(f.to)}</div>
                  </div>

                  <div style={{textAlign:"right"}}>
                    <div style={styles.price}>${f.price.toFixed(2)}</div>
                    <div style={{fontSize:12, color:"#666"}}>★ {f.rating.toFixed(1)}</div>
                  </div>
                </div>

                <div style={{marginTop:8, display:"flex", gap:12, alignItems:"center"}}>
                  {f.amenities.wifi && <span style={styles.amenity}>WiFi</span>}
                  {f.amenities.meals && <span style={styles.amenity}>Meals</span>}
                  {f.amenities.entertainment && <span style={styles.amenity}>Entertainment</span>}
                  <button style={styles.selectBtn}>Select Flight</button>
                </div>
              </article>
            ))}
          </div>
        </div>
      </main>

      <footer style={styles.footer}>
        <div style={styles.container}>
          <small>Customer Support: +1 (518) 555-0123</small>
        </div>
      </footer>
    </div>
  );
}

// ---- Helpers ----
function toMinutes(hhmm) {
  const [h, m] = hhmm.split(":").map(Number);
  return h * 60 + m;
}
function formatDuration(min) {
  const h = Math.floor(min / 60);
  const m = min % 60;
  return `${h}h ${m}m`;
}
function cityFromCode(code) {
  const map = { JFK: "New York (JFK)", LGA: "New York (LGA)", EWR: "Newark (EWR)", LHR: "London (LHR)", SFO: "San Francisco (SFO)", MIA: "Miami (MIA)", LAX: "Los Angeles (LAX)", ALB: "Albany (ALB)", DFW: "Dallas (DFW)", DEN: "Denver (DEN)", CDG: "Paris (CDG)", MCO: "Orlando (MCO)" };
  return map[code] || code;
}
function airportName(code) { return code; }

// ---- Styles (kept compatible with your current file) ----
const styles = {
  page: { fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, sans-serif", color: "#111" },
  container: { maxWidth: 1000, margin: "0 auto", padding: "0 18px" },

  header: { background: "#f7fbff", borderBottom: "1px solid #e9eef5" },
  brand: { fontWeight: 800, fontSize: 28, padding: "16px 0" },
  nav: { display: "flex", gap: 18 },
  link: { textDecoration: "none", color: "#333", padding: "14px 0" },

  hero: { background: "#f6f7fb", padding: "28px 0 40px" },
  heroInner: { display: "grid", gap: 16 },
  title: { margin: 0, fontSize: 24 },

  card: { background: "#fff", border: "1px solid #e6e6e6", borderRadius: 12, padding: 16, display: "grid", gap: 12, boxShadow: "0 1px 2px rgba(0,0,0,.04)" },
  row: { display: "grid", gridTemplateColumns: "1fr 1fr", gap: 12 },
  label: { display: "grid", gap: 6, fontSize: 12, color: "#444" },
  input: { height: 38, padding: "0 10px", borderRadius: 8, border: "1px solid #cfd7df", fontSize: 14, background:"#fff" },
  button: { height: 40, border: "1px solid #2f6feb", background: "#2f6feb", color: "white", borderRadius: 8, fontWeight: 600, cursor: "pointer" },

  resultCard: { background:"#fff", border:"1px solid #e7ebf0", borderRadius:12, padding:16, boxShadow:"0 1px 2px rgba(0,0,0,.04)" },
  time: { fontSize:16, fontWeight:700 },
  price: { fontSize:18, fontWeight:800 },
  badge: { fontSize:12, background:"#eef3ff", color:"#2f6feb", padding:"2px 6px", borderRadius:6, marginLeft:6 },
  amenity: { fontSize:12, color:"#4b5563", background:"#f3f4f6", padding:"4px 8px", borderRadius:6 },
  selectBtn: { marginLeft:"auto", border:"1px solid #2f6feb", background:"#2f6feb", color:"#fff", borderRadius:8, height:34, padding:"0 12px", cursor:"pointer" },

  footer: { borderTop: "1px solid #eee", padding: "16px 0", color: "#666", background: "#fff" },
};
