// src/CheckIn.js
import { Link } from "react-router-dom";

import { useMemo, useState } from "react";

export default function CheckIn() {
  const [code, setCode] = useState("");
  const [last, setLast] = useState("");
  const [result, setResult] = useState(null);
  const [error, setError] = useState("");

  // Dummy reservations
  const reservations = useMemo(
    () => [
      {
        confirmation: "DL7K3Q",
        last: "Irfan",
        first: "Saba",
        airline: "Delta",
        flightNo: "DL 1234",
        from: "JFK",
        to: "LHR",
        departIso: "2025-11-20T08:30:00-05:00", // local time
        checkedIn: false,
      },
      {
        confirmation: "UA55AA",
        last: "Syed",
        first: "Malka",
        airline: "United",
        flightNo: "UA 22",
        from: "EWR",
        to: "SFO",
        departIso: "2025-11-20T07:10:00-05:00",
        checkedIn: true,
      },
    ],
    []
  );

  function findReservation(e) {
    e.preventDefault();
    setError("");
    const hit = reservations.find(
      r =>
        r.confirmation.toUpperCase() === code.trim().toUpperCase() &&
        r.last.toLowerCase() === last.trim().toLowerCase()
    );
    if (!hit) {
      setResult(null);
      setError("No reservation found. Check your code and last name.");
      return;
    }
    setResult({ ...hit });
  }

  function canCheckIn(depIso) {
    const dep = new Date(depIso).getTime();
    const now = Date.now();
    const open = dep - 24 * 60 * 60 * 1000; // 24h before
    return now >= open && now < dep;
  }

  function formatDT(depIso) {
    const d = new Date(depIso);
    return d.toLocaleString([], { dateStyle: "medium", timeStyle: "short" });
  }

  function handleCheckIn() {
    // In a real app you’d POST to backend. Here we just set state.
    setResult(prev => ({ ...prev, checkedIn: true }));
  }

  const eligible = result ? canCheckIn(result.departIso) : false;

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <div style={styles.container}>
          <div style={styles.brand}>LiftOff</div>
          <nav style={styles.nav}>
  <a style={styles.link} href="/home">Book</a>
  <a style={styles.link} href="/check-in">Check-in</a>
  <a style={{ ...styles.link, fontWeight: 700 }} href="/profile">Profile</a>
</nav>

        </div>
      </header>

      <main style={styles.main}>
        <div style={styles.container}>
          <h1 style={{ marginTop: 0 }}>Check-In</h1>

          <form onSubmit={findReservation} style={styles.card}>
            <label style={styles.label}>
              Confirmation code
              <input
                style={styles.input}
                placeholder="e.g., DL7K3Q"
                value={code}
                onChange={e => setCode(e.target.value)}
              />
            </label>

            <label style={styles.label}>
              Last name
              <input
                style={styles.input}
                placeholder="e.g., Irfan"
                value={last}
                onChange={e => setLast(e.target.value)}
              />
            </label>

            <button style={styles.button} type="submit">Find my trip</button>

            {error && <div style={styles.error}>{error}</div>}
          </form>

          {result && (
            <section style={styles.card}>
              <div style={{ display: "grid", gap: 6 }}>
                <div style={{ fontWeight: 700 }}>
                  {result.airline} <span style={styles.badge}>{result.flightNo}</span>
                </div>
                <div>
                  {result.from} → {result.to}
                </div>
                <div>Departure: {formatDT(result.departIso)}</div>
                <div>
                  Status:&nbsp;
                  {result.checkedIn ? (
                    <strong style={{ color: "#059669" }}>Checked in ✅</strong>
                  ) : eligible ? (
                    <strong style={{ color: "#2563eb" }}>Check-in available</strong>
                  ) : (
                    <span style={{ color: "#6b7280" }}>
                      Opens 24h before departure
                    </span>
                  )}
                </div>
              </div>

              {!result.checkedIn && eligible && (
                <button style={{ ...styles.button, marginTop: 12 }} onClick={handleCheckIn}>
                  Check in now
                </button>
              )}

              {!eligible && !result.checkedIn && (
                <small style={{ color: "#6b7280", marginTop: 8, display: "block" }}>
                  Tip: change your system clock forward (for demo) to simulate the window.
                </small>
              )}
            </section>
          )}
        </div>
      </main>

      <footer style={styles.footer}>
        <div style={styles.container}>
          <small>Need help? Call +1 (518) 555-0123</small>
        </div>
      </footer>
    </div>
  );
}

const styles = {
  page: { fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, sans-serif", color: "#111" },
  container: { maxWidth: 900, margin: "0 auto", padding: "0 18px" },
  header: { background: "#f7fbff", borderBottom: "1px solid #e9eef5" },
  brand: { fontWeight: 800, fontSize: 28, padding: "16px 0" },
  nav: { display: "flex", gap: 18 },
  link: { textDecoration: "none", color: "#333", padding: "14px 0" },

  main: { background: "#f6f7fb", minHeight: "60vh", padding: "28px 0 40px" },
  card: { background: "#fff", border: "1px solid #e6e6e6", borderRadius: 12, padding: 16, display: "grid", gap: 12, boxShadow: "0 1px 2px rgba(0,0,0,.04)", marginTop: 12 },

  label: { display: "grid", gap: 6, fontSize: 12, color: "#444" },
  input: { height: 38, padding: "0 10px", borderRadius: 8, border: "1px solid #cfd7df", fontSize: 14, background: "#fff" },
  button: { height: 40, border: "1px solid #2563eb", background: "#2563eb", color: "white", borderRadius: 8, fontWeight: 600, cursor: "pointer" },
  badge: { fontSize: 12, background: "#eef3ff", color: "#2563eb", padding: "2px 6px", borderRadius: 6, marginLeft: 6 },
  error: { color: "#b91c1c", background: "#fee2e2", border: "1px solid #fecaca", padding: 10, borderRadius: 8 },
  footer: { borderTop: "1px solid #eee", padding: "16px 0", color: "#666", background: "#fff" },
};
