import React, { useState } from "react";
import "./Login.css";
import { useNavigate } from "react-router-dom";

const Login = () => {
  const [showPassword, setShowPassword] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const [error, setError] = useState("");
  const [showSuccess, setShowSuccess] = useState(false);

  const navigate = useNavigate();

  const togglePassword = () => setShowPassword(!showPassword);

  const goToSignUp = () => {
    navigate("/SignUpPage");
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validation
    if (!email.trim() || !password.trim()) {
      setError("Please fill in all required fields.");
      return;
    }

    // Clear any existing error messages
    setError("");

    // Show success popup
    setShowSuccess(true);

    // Simulate login delay then redirect
    setTimeout(() => {
      setShowSuccess(false);
      navigate("/home"); // Change route if needed
    }, 1500);
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <div className="neu-icon">
            <div className="icon-inner">
              <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2" />
                <circle cx="12" cy="7" r="4" />
              </svg>
            </div>
          </div>

          <div className="login-header">
            <h2 className="logo-text">
              <span>Liftoff</span>
            </h2>
            <p className="login-subtext">Login or create an account</p>
          </div>
        </div>

        <form className="login-form" onSubmit={handleSubmit} noValidate>
          {/* EMAIL */}
          <div className="form-group">
            <label htmlFor="email" style={{ display: "block", color: "#3d4468", fontWeight: 600 }}>
              Email
            </label>

            <div className="input-group neu-input">
              <input
                type="email"
                id="email"
                name="email"
                placeholder=" "
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
              <label htmlFor="email">Email address</label>

              <div className="input-icon">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M4 4h16v16H4z" />
                  <polyline points="22,6 12,13 2,6" />
                </svg>
              </div>
            </div>
          </div>

          {/* PASSWORD */}
          <div className="form-group">
            <label htmlFor="password" style={{ display: "block", color: "#3d4468", fontWeight: 600 }}>
              Password
            </label>

            <div className="input-group neu-input password-group">
              <input
                type={showPassword ? "text" : "password"}
                id="password"
                name="password"
                placeholder=" "
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
              <label htmlFor="password">Password</label>

              <div className="input-icon">
                <svg viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <rect x="3" y="11" width="18" height="11" rx="2" ry="2" />
                  <path d="M7 11V7a5 5 0 0110 0v4" />
                </svg>
              </div>

              <button
                type="button"
                className={`neu-toggle ${showPassword ? "show-password" : ""}`}
                onClick={togglePassword}
              >
                <svg className="eye-open" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M1 12s4-8 11-8 11 8 11 8-4 8-11 8-11-8-11-8z" />
                  <circle cx="12" cy="12" r="3" />
                </svg>

                <svg className="eye-closed" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2">
                  <path d="M17.94 17.94A10.07 10.07 0 0 1 12 20c-7 0-11-8-11-8a18.45 18.45 0 0 1 5.06-5.94" />
                  <line x1="1" y1="1" x2="23" y2="23" />
                </svg>
              </button>
            </div>
          </div>

          {/* ERROR MESSAGE */}
          {error && (
            <p style={{ color: "#e25454", fontWeight: 600, marginBottom: "12px", textAlign: "center" }}>
              {error}
            </p>
          )}

          {/* Login Button */}
          <button type="submit" className="neu-button login-btn">
            <span className="btn-text">Log In</span>
            <div className="btn-loader">
              <div className="neu-spinner"></div>
            </div>
          </button>

          {/* Signup Button */}
          <button type="button" className="neu-button signup-btn" onClick={goToSignUp}>
            <span className="btn-text">Sign up</span>
          </button>
        </form>
      </div>

      {/* SUCCESS POPUP */}
      {showSuccess && (
        <div style={styles.overlay}>
          <div style={styles.successCard}>
            <h3 style={{ marginBottom: 10 }}>Login Successful!</h3>
            <p>You will be redirected shortly...</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Login;

// SUCCESS POPUP STYLES
const styles = {
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    width: "100vw",
    height: "100vh",
    background: "rgba(0, 0, 0, 0.45)",
    display: "flex",
    justifyContent: "center",
    alignItems: "center",
    zIndex: 9999,
  },

  successCard: {
    background: "white",
    padding: "25px 35px",
    borderRadius: "14px",
    boxShadow: "0 6px 14px rgba(0,0,0,0.2)",
    textAlign: "center",
    fontFamily: "system-ui",
  },
};
