import React, { useState } from "react";
import "./UserDashboard.css";

function Support() {
  // Track which FAQ is currently open
  const [openIndex, setOpenIndex] = useState(null);

  // Example FAQs
  const faqs = [
    {
      question: "How can I change my flight date?",
      answer:
        "You can change your flight date by visiting the 'My Flights' tab and selecting the flight you wish to modify. Look for the 'Change Flight' option if your fare allows changes.",
    },
    {
      question: "How do I earn reward points?",
      answer:
        "You earn reward points for every completed flight. The number of points depends on your ticket class and distance flown. Check the 'My Points' tab to track your rewards.",
    },
    {
      question: "Can I check in without my confirmation code?",
      answer:
        "You can check in using your last name and frequent flyer number if you’ve lost your confirmation code. Go to the 'Check-In' tab for more details.",
    },
    {
      question: "How do I contact customer service?",
      answer:
        "Our team is available 24/7 through this support form or by calling +1 (518) 555-0123. We usually respond to tickets within a few hours.",
    },
  ];

  return (
    <div className="user-page support">
      {/* ===== HEADER ===== */}
      <h2 className="section-title">Customer Support</h2>
      <p className="support-text">
        Have questions or need help? We’re here for you 24/7.
      </p>

      {/* ===== SUPPORT FORM ===== */}
      <form className="support-form">
        <input type="text" placeholder="Your Name" />
        <textarea placeholder="Describe your issue..." rows="4" />
        <button type="submit">Submit Ticket</button>
      </form>

      {/* ===== FAQ SECTION ===== */}
      <div className="faq-section">
        <h3 className="faq-title">Frequently Asked Questions</h3>
        <div className="faq-list">
          {faqs.map((faq, index) => (
            <div
              key={index}
              className={`faq-item ${openIndex === index ? "open" : ""}`}
            >
              <div
                className="faq-question"
                onClick={() =>
                  setOpenIndex(openIndex === index ? null : index)
                }
              >
                <span>{faq.question}</span>
                <span className="faq-icon">
                  {openIndex === index ? "−" : "+"}
                </span>
              </div>
              {openIndex === index && (
                <div className="faq-answer">{faq.answer}</div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Support;
