import React, { useState } from 'react';
import './Admin.css';

export default function Admin(){
  const [tab, setTab] = useState('users');
  const [users, setUsers] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [tickets, setTickets] = useState([]);
  const [userQuery, setUserQuery] = useState('');
  const [bookingQuery, setBookingQuery] = useState('');

  async function fetchUsers(){
  const url = userQuery ? `http://localhost:9000/admin/users?q=${encodeURIComponent(userQuery)}` : 'http://localhost:9000/admin/users';
  const res = await fetch(url);
    const json = await res.json();
    setUsers(json.users || []);
  }

  async function fetchBookings(){
  const url = bookingQuery ? `http://localhost:9000/admin/bookings?q=${encodeURIComponent(bookingQuery)}` : 'http://localhost:9000/admin/bookings';
  const res = await fetch(url);
    const json = await res.json();
    setBookings(json.bookings || []);
  }

  async function fetchTickets(){
    const res = await fetch('http://localhost:9000/admin/tickets');
    const json = await res.json();
    setTickets(json.tickets || []);
  }

  React.useEffect(()=>{ fetchUsers(); fetchBookings(); fetchTickets(); }, []);

  function refreshAll(){ fetchUsers(); fetchBookings(); fetchTickets(); }

  async function deleteUser(id){
    if(!window.confirm('Delete user? This cannot be undone')) return;
    await fetch(`http://localhost:9000/admin/user/${id}`, { method: 'DELETE' });
    fetchUsers();
  }

  async function deleteBooking(id){
    if(!window.confirm('Delete booking?')) return;
    await fetch(`http://localhost:9000/admin/booking/${id}`, { method: 'DELETE' });
    fetchBookings();
  }

  return (
    <div className="admin-page-root">
      <div className="admin-hero">
        <div className="admin-hero-inner container">
          <div className="admin-hero-header">
            <h1>Admin Console</h1>
            <p className="muted">Manage users, bookings, airlines, sellers, and reports.</p>
          </div>

          <div className="cards-container">
            <div className="admin-cards-grid">
              <div className="admin-card">
                <div style={{display:'flex', justifyContent:'space-between', alignItems:'center', gap: 12}}>
                  <div>
                    <h3>Users</h3>
                    <p className="muted">View and manage registered users.</p>
                  </div>
                  <div style={{display:'flex', gap:8}}>
                    <input placeholder="Search users by name, username, id" value={userQuery} onChange={e=>setUserQuery(e.target.value)} />
                    <button className="btn-secondary" onClick={fetchUsers}>Search</button>
                    <button className="btn-secondary" onClick={()=>{ setUserQuery(''); fetchUsers(); }}>Clear</button>
                  </div>
                </div>
                <div className="admin-list">
                  {users.map(u=> (
                    <div className="admin-list-row" key={u._id}>
                      <div className="subcard">
                        <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
                          <div>
                            <strong>{u.username || (u.firstName+' '+(u.lastName||''))}</strong>
                            <div className="muted">Points: {u.totalPoints || 0} • Id: {u._id}</div>
                          </div>
                          <div className="row-actions">
                            <button onClick={()=>deleteUser(u._id)}>Delete</button>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
              <div className="admin-card">
                <div style={{display:'flex', justifyContent:'space-between', alignItems:'center', gap: 12}}>
                  <div>
                    <h3>Bookings</h3>
                    <p className="muted">View and manage flight bookings.</p>
                  </div>
                  <div style={{display:'flex', gap:8}}>
                    <input placeholder="Search bookings by code, name, email, route" value={bookingQuery} onChange={e=>setBookingQuery(e.target.value)} />
                    <button className="btn-secondary" onClick={fetchBookings}>Search</button>
                    <button className="btn-secondary" onClick={()=>{ setBookingQuery(''); fetchBookings(); }}>Clear</button>
                  </div>
                </div>
                <div className="admin-list">
                  {bookings.map(b => (
                    <div className="admin-list-row" key={b._id}>
                      <div className="subcard">
                        <div style={{display:'flex', justifyContent:'space-between', alignItems:'flex-start', gap: '12px'}}>
                          <div style={{flex:1}}>
                            <strong>{b.confirmationCode}</strong>
                            <div className="muted">{b.name} • {b.email} • {b.date} • {b.from} → {b.to}</div>
                            <div className="muted">Price: ${b.price || 0} • Checked-in: {b.checkedIn ? 'Yes' : 'No'}</div>
                            <div className="muted">User: {b.userId ? (b.userId.username || (b.userId.firstName+' '+(b.userId.lastName||''))) : (b.guestId || 'Guest')}</div>
                            {b.flightId && <div className="muted">Flight: {b.flightId.airline} {b.flightId.flightNo} — {b.flightId.from} → {b.flightId.to}</div>}
                          </div>
                          <div className="row-actions">
                            <button onClick={()=>deleteBooking(b._id)}>Delete</button>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
              <div className="admin-card">
                <h3>Airlines</h3>
                <p className="muted">Manage airline partners.</p>
              </div>
              <div className="admin-card">
                <h3>Sellers</h3>
                <p className="muted">Seller accounts and listings.</p>
              </div>
              <div className="admin-card">
                <h3>Reports</h3>
                <p className="muted">Site usage, bookings, and revenue reports.</p>
                <div style={{display:'flex', justifyContent:'space-between', alignItems:'center'}}>
                  <div>
                    <h3>Reports</h3>
                    <p className="muted">Site usage, bookings, and revenue reports.</p>
                  </div>
                  <div>
                    <button className="btn-secondary" onClick={fetchTickets}>Refresh Tickets</button>
                  </div>
                </div>
                <div className="admin-list">
                  <h4>Tickets</h4>
                  {tickets.map(t => (
                    <div className="admin-list-row" key={t._id}>
                      <div className="subcard">
                        <div style={{display:'flex', justifyContent:'space-between', alignItems:'flex-start'}}>
                          <div style={{flex:1}}>
                            <strong>{t.email}</strong>
                            <div className="muted">{new Date(t.createdAt).toLocaleString()} • Id: {t._id}</div>
                            <div style={{marginTop:8}}>{t.message}</div>
                          </div>
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  )
}
