import React, { useState } from "react";
import "../Login.css"; // Reuse same neumorphic styling

const PaymentPage = () => {
  // Passenger Details
  const [passengerCount, setPassengerCount] = useState(1);
  const [seatingPreference, setSeatingPreference] = useState("together");

  const [passengerList, setPassengerList] = useState([
    { fullName: "", age: "" }
  ]);

  // Contact + Payment Details
  const [email, setEmail] = useState("");
  const [cardName, setCardName] = useState("");
  const [cardNumber, setCardNumber] = useState("");
  const [expiry, setExpiry] = useState("");
  const [cvv, setCvv] = useState("");

  // Confirm + Loader states
  const [isLoading, setIsLoading] = useState(false);
  const [isConfirmed, setIsConfirmed] = useState(false);
  const [message, setMessage] = useState("");

  // Base price
  const basePrice = 108.99;
  const tax = 8.25;
  const total = ((basePrice * passengerCount) + tax).toFixed(2);

  // ===== Handle passenger count change =====
  const handlePassengerCountChange = (e) => {
    const count = parseInt(e.target.value);
    setPassengerCount(count);

    let updated = [...passengerList];

    if (count > updated.length) {
      for (let i = updated.length; i < count; i++) {
        updated.push({ fullName: "", age: "" });
      }
    } else {
      updated = updated.slice(0, count);
    }

    setPassengerList(updated);
  };

  // ===== Submit Form =====
  const handleSubmit = (e) => {
    e.preventDefault();

    if (!email || !cardName || !cardNumber || !expiry || !cvv) {
      setMessage("⚠️ Please fill all required fields.");
      return;
    }

    for (let p of passengerList) {
      if (!p.fullName || !p.age) {
        setMessage("⚠️ Please complete all passenger details.");
        return;
      }
    }

    // Show loader
    setIsLoading(true);

    setTimeout(() => {
      setIsLoading(false);
      setIsConfirmed(true);
      setMessage("Payment successful!");
    }, 2500);
  };

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        background: "url('/public/img2.jpg') no-repeat center center/cover",
        padding: "40px",
      }}
    >
      <div
        className="login-card"
        style={{
          width: "650px",
          maxHeight: "95vh",
          overflowY: "auto",
          padding: "55px 70px",
          background: "rgba(255, 255, 255, 0.88)",
          borderRadius: "35px",
          boxShadow: "25px 25px 60px #c5c9d1, -25px -25px 60px #ffffff",
        }}
      >
        {/* Hide scrollbar */}
        <style>
          {`
            .login-card::-webkit-scrollbar { display: none; }
            @keyframes spin { 0%{transform:rotate(0deg);} 100%{transform:rotate(360deg);} }
          `}
        </style>

        {/* ========================= */}
        {/* LOADING SCREEN */}
        {/* ========================= */}
        {isLoading ? (
          <div style={{ textAlign: "center", padding: "100px 0" }}>
            <div
              style={{
                width: "70px",
                height: "70px",
                border: "6px solid #cfd4db",
                borderTop: "6px solid #28a745",
                borderRadius: "50%",
                animation: "spin 1s linear infinite",
                margin: "0 auto 20px",
              }}
            ></div>
            <h2 style={{ color: "#3d4468" }}>Processing your payment...</h2>
          </div>
        ) : isConfirmed ? (
          /* ========================= */
          /* CONFIRMATION SCREEN */
          /* ========================= */
          <div style={{ textAlign: "center", padding: "100px 20px" }}>
            <h1 style={{ color: "#28a745", fontSize: "2.2rem", marginBottom: "20px" }}>
              Payment Successful!
            </h1>
            <p style={{ color: "#3d4468", fontSize: "1.1rem" }}>
              Thank you for booking with <strong>LiftOff Airlines</strong>.
            </p>
            <p style={{ color: "#3d4468", marginTop: "10px" }}>
              A receipt has been sent to <strong>{email}</strong>.
            </p>
          </div>
        ) : (
          <>
            {/* ========================= */}
            {/* PASSENGER COUNT + SEATING */}
            {/* ========================= */}
            <section>
              <div className="login-header">
                <h2 style={{ textAlign: "center", marginBottom: "4px" }}>
                  Booking Details
                </h2>
                <p style={{ textAlign: "center" }}>Select the number of passengers.</p>
              </div>

              {/* Passenger Count */}
              <div className="form-group">
                <label style={{ fontWeight: "600", color: "#3d4468" }}>
                  Number of Passengers
                </label>

                <div className="neu-input">
                  <select
                    value={passengerCount}
                    onChange={handlePassengerCountChange}
                    style={{
                      width: "100%",
                      padding: "12px",
                      borderRadius: "15px",
                      background: "transparent",
                      border: "none",
                      outline: "none",
                    }}
                  >
                    {Array.from({ length: 10 }, (_, i) => (
                      <option key={i + 1} value={i + 1}>
                        {i + 1}
                      </option>
                    ))}
                  </select>
                </div>
              </div>

              {/* Seating Preference */}
              <div className="form-group" style={{ marginTop: "15px" }}>
                <label style={{ fontWeight: "600", color: "#3d4468" }}>
                  Seating Preference
                </label>

                <div style={{ display: "flex", gap: "20px", marginTop: "10px" }}>
                  <label style={{ display: "flex", gap: "8px", alignItems: "center" }}>
                    <input
                      type="radio"
                      value="together"
                      checked={seatingPreference === "together"}
                      onChange={(e) => setSeatingPreference(e.target.value)}
                    />
                    Sit Together
                  </label>

                  <label style={{ display: "flex", gap: "8px", alignItems: "center" }}>
                    <input
                      type="radio"
                      value="separate"
                      checked={seatingPreference === "separate"}
                      onChange={(e) => setSeatingPreference(e.target.value)}
                    />
                    Sit Separate
                  </label>
                </div>
              </div>
            </section>

            {/* ========================= */}
            {/* PASSENGER INFORMATION */}
            {/* ========================= */}
            <section style={{ marginTop: "40px" }}>
              <h2 style={{ textAlign: "center", marginBottom: "10px" }}>
                Passenger Information
              </h2>
              <p style={{ textAlign: "center", marginBottom: "20px" }}>
                Enter details for all passengers.
              </p>

              {passengerList.map((p, index) => (
                <div
                  key={index}
                  style={{
                    marginBottom: "30px",
                    padding: "25px",
                    borderRadius: "20px",
                    background: "rgba(255,255,255,0.6)",
                    boxShadow: "inset 8px 8px 16px #cfd4db, inset -8px -8px 16px #ffffff",
                  }}
                >
                  <h3 style={{ marginBottom: "12px", color: "#3d4468" }}>
                    Passenger {index + 1}
                  </h3>

                  {/* Full Name */}
                  <div className="form-group">
                    <label style={{ fontWeight: "600", color: "#3d4468" }}>
                      Full Name
                    </label>
                    <div className="neu-input">
                      <input
                        type="text"
                        placeholder=" "
                        value={p.fullName}
                        onChange={(e) => {
                          const updated = [...passengerList];
                          updated[index].fullName = e.target.value;
                          setPassengerList(updated);
                        }}
                      />
                      <label>Full Name</label>
                    </div>
                  </div>

                  {/* Age */}
                  <div className="form-group" style={{ marginTop: "15px" }}>
                    <label style={{ fontWeight: "600", color: "#3d4468" }}>
                      Age
                    </label>
                    <div className="neu-input">
                      <input
                        type="number"
                        placeholder=" "
                        min="0"
                        value={p.age}
                        onChange={(e) => {
                          const updated = [...passengerList];
                          updated[index].age = e.target.value;
                          setPassengerList(updated);
                        }}
                      />
                      <label>Age</label>
                    </div>
                  </div>
                </div>
              ))}
            </section>

            {/* ========================= */}
            {/* CONTACT + PAYMENT */}
            {/* ========================= */}
            <section>
              <h2 style={{ textAlign: "center", marginBottom: "8px" }}>
                Payment Information
              </h2>

              {/* Email */}
              <div className="form-group">
                <label style={{ fontWeight: "600", color: "#3d4468" }}>
                  Email for Receipt
                </label>
                <div className="neu-input">
                  <input
                    type="email"
                    placeholder=" "
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                  />
                  <label>Email Address</label>
                </div>
              </div>

              {/* Name on Card */}
              <div className="form-group" style={{ marginTop: "15px" }}>
                <label style={{ fontWeight: "600", color: "#3d4468" }}>
                  Name on Card
                </label>
                <div className="neu-input">
                  <input
                    type="text"
                    placeholder=" "
                    value={cardName}
                    onChange={(e) => setCardName(e.target.value)}
                  />
                  <label>Name on Card</label>
                </div>
              </div>

              {/* Card Number */}
              <div className="form-group" style={{ marginTop: "15px" }}>
                <label style={{ fontWeight: "600", color: "#3d4468" }}>
                  Card Number
                </label>
                <div className="neu-input">
                  <input
                    type="text"
                    placeholder=" "
                    maxLength="16"
                    value={cardNumber}
                    onChange={(e) => setCardNumber(e.target.value)}
                  />
                  <label>Card Number</label>
                </div>
              </div>

              {/* Expiry + CVV */}
              <div className="form-group" style={{ display: "flex", gap: "15px", marginTop: "15px" }}>
                <div style={{ flex: 1 }}>
                  <label style={{ fontWeight: "600", color: "#3d4468" }}>
                    Expiration
                  </label>
                  <div className="neu-input">
                    <input
                      type="text"
                      placeholder=" "
                      maxLength="5"
                      value={expiry}
                      onChange={(e) => setExpiry(e.target.value)}
                    />
                    <label>MM/YY</label>
                  </div>
                </div>

                <div style={{ flex: 1 }}>
                  <label style={{ fontWeight: "600", color: "#3d4468" }}>
                    CVV
                  </label>
                  <div className="neu-input">
                    <input
                      type="password"
                      placeholder=" "
                      maxLength="4"
                      value={cvv}
                      onChange={(e) => setCvv(e.target.value)}
                    />
                    <label>CVV</label>
                  </div>
                </div>
              </div>
            </section>

            {/* ========================= */}
            {/* SUMMARY */}
            {/* ========================= */}
            <section style={{ marginTop: "40px" }}>
              <h2 style={{ textAlign: "center", marginBottom: "12px" }}>
                Trip Summary
              </h2>

              <div
                style={{
                  background: "rgba(255,255,255,0.6)",
                  padding: "25px",
                  borderRadius: "20px",
                  boxShadow: "inset 8px 8px 16px #cfd4db, inset -8px -8px 16px #ffffff",
                }}
              >
                <h3 style={{ color: "#3d4468" }}>Passengers</h3>

                {passengerList.map((p, i) => (
                  <p
                    key={i}
                    style={{ marginLeft: "25px", color: "#7f8087ff", fontWeight: "600" }}
                  >
                    Passenger {i + 1}: {p.fullName || "—"} (Age {p.age || "—"})
                  </p>
                ))}

                <div
                  style={{
                    height: "2px",
                    background: "linear-gradient(90deg, transparent, #bec3cf, transparent)",
                    margin: "20px 0",
                  }}
                ></div>

                <h3 style={{ color: "#3d4468" }}>Cost</h3>
                <p style={{ marginLeft: "25px", color: "#7f8087ff", fontWeight: "600" }}>
                  Base Fare: ${basePrice} × {passengerCount}  
                  <br />
                  Tax: ${tax}
                  <br />
                  <strong>Total: ${total}</strong>
                </p>
              </div>

              {/* Confirm + Submit */}
              <button
                onClick={handleSubmit}
                style={{
                  marginTop: "30px",
                  width: "100%",
                  background: "#28a745",
                  padding: "18px",
                  borderRadius: "15px",
                  color: "white",
                  fontWeight: "600",
                  cursor: "pointer",
                  border: "none",
                }}
              >
                Submit & Finalize
              </button>

              {message && (
                <p style={{ textAlign: "center", marginTop: "10px", color: "#3d4468" }}>
                  {message}
                </p>
              )}
            </section>
          </>
        )}
      </div>
    </div>
  );
};

export default PaymentPage;
