// ---- Helper functions ----
function toMinutes(hhmm) {
  const [h, m] = hhmm.split(":").map(Number);
  return h * 60 + m;
}
function formatDuration(min) {
  const h = Math.floor(min / 60);
  const m = min % 60;
  return `${h}h ${m}m`;
}
function cityFromCode(code) {
  const map = { JFK: "New York (JFK)", LGA: "New York (LGA)", EWR: "Newark (EWR)", LHR: "London (LHR)", SFO: "San Francisco (SFO)", MIA: "Miami (MIA)", LAX: "Los Angeles (LAX)", ALB: "Albany (ALB)", DFW: "Dallas (DFW)", DEN: "Denver (DEN)", CDG: "Paris (CDG)", MCO: "Orlando (MCO)" };
  return map[code] || code;
}
function airportName(code) { return code; }

//DEBUG from here- LOCATION FILTER LAX - lax etc.
function locationName(input, code){
  if(!input)
    return true; //if the user didnt type anything like when the input is empty, we'll just act as it is a match to show the flights still.
  const typed = input.trim().toLowerCase(); //case + space insenstiive. 
  const lowerCaseCode = code.toLowerCase(); //let's say LAX can also be lax. doesnt matter if the code is all lower case or capital.
  const city = cityFromCode(code).toLowerCase(); //city names can also be lower case like los angeles miami etc.
  return lowerCaseCode.includes(typed) || city.includes(typed);
} //DEBUG to here.

