import { useNavigate } from "react-router-dom";
import { useEffect, useState } from "react";

export default function SellerProfilePage() {
  const navigate = useNavigate();
  const [seller, setSeller] = useState(null);
  const [editingPhone, setEditingPhone] = useState('');
  const [editingLocation, setEditingLocation] = useState('');
  // Fetch seller data for the logged-in user
  useEffect(() => {
    async function load() {
      try {
        const raw = localStorage.getItem('liftoffUser');
        if (!raw) {
          // no session — send to login
          return navigate('/Login');
        }
        const session = JSON.parse(raw);
        const userId = session._id || session.userId || session.id;
        if (!userId) {
          // fallback: if we only have username, try to redirect to home
          return navigate('/');
        }

        // Prefer seller application document if available (contains business data)
        const username = session.username || session.email || session.user;
        if (username) {
          try {
            const appRes = await fetch(`http://localhost:9000/seller/application/byEmail?email=${encodeURIComponent(username)}`);
            const appJson = await appRes.json();
            if (appJson && appJson.application) {
                  setSeller(appJson.application);
                  setEditingPhone(appJson.application.phone || appJson.application.phoneNumber || '');
                  setEditingLocation(appJson.application.location || appJson.application.officeLocation || '');
                  return;
                }
          } catch (e) {
            // continue to fallback
            console.warn('Seller application fetch failed', e);
          }
        }

        const res = await fetch(`http://localhost:9000/user/${userId}`);
        if (!res.ok) {
          console.error('Failed to load user', res.status);
          return setSeller({ error: 'Unable to load profile' });
        }
        const json = await res.json();
        // backend returns { user }
        const u = json.user || json;
  setSeller(u);
  setEditingPhone(u.phone || u.phoneNumber || '');
  setEditingLocation(u.location || u.officeLocation || '');
      } catch (err) {
        console.error('Profile load error', err);
        setSeller({ error: 'Unable to load profile' });
      }
    }
    load();
  }, [navigate]);

  if (!seller) return <div style={{ padding: 30 }}>Loading…</div>;
  if (seller && seller.error) return <div style={{ padding: 30 }}>{seller.error}</div>;

  return (
    <div style={styles.page}>
      <main style={styles.hero}>
        <div style={styles.heroInner}>

          <h2 style={styles.title}>Seller Profile</h2>

          <div style={styles.sectionCard}>

            <div style={styles.infoGrid}>
              <p><strong>Full Name:</strong> {(
                seller.fullName ||
                ((seller.firstName || '').trim() + (seller.lastName ? ' ' + (seller.lastName || '').trim() : '')).trim() ||
                seller.name ||
                seller.username ||
                '—'
              )}</p>
              <p><strong>Organization:</strong> {seller.orgName || seller.businessName || seller.organizationName || '—'}</p>
              <p><strong>Business #:</strong> {seller.businessNumber || '—'}</p>
              <p><strong>Email:</strong> {seller.username || seller.businessEmail || '—'}</p>
              <p>
                <strong>Phone:</strong>
                <input value={editingPhone} onChange={e=>setEditingPhone(e.target.value)} style={{marginLeft:8, padding:'6px 8px', width:'100%'}} />
              </p>
              <p><strong>Role:</strong> {(seller.isSeller || seller.status === 'approved') ? 'Seller' : (seller.role || 'User')}</p>
              <p>
                <strong>Office Location:</strong>
                <input value={editingLocation} onChange={e=>setEditingLocation(e.target.value)} style={{marginLeft:8, padding:'6px 8px', width:'100%'}} />
              </p>
              <p><strong>Joined:</strong> {seller.createdAt ? new Date(seller.createdAt).toLocaleDateString() : '—'}</p>
            </div>

            <button
              style={styles.backBtn}
              onClick={() => navigate("/seller-dashboard")}
            >
              ← Back to Flights
            </button>

            <div style={{marginTop:12}}>
              <button style={styles.editBtn} onClick={async () => {
                // If seller document has an _id (from application) call patch
                try{
                  if (!seller || !seller._id) return alert('Unable to save: missing seller id');
                  const res = await fetch(`http://localhost:9000/seller/application/${seller._id}`, {
                    method: 'PATCH',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ phone: editingPhone, location: editingLocation })
                  });
                  const json = await res.json();
                  if (res.ok && json.application) {
                    setSeller(json.application);
                    alert('Profile updated');
                  } else {
                    alert('Failed to update profile');
                  }
                }catch(err){ console.error(err); alert('Error saving profile'); }
              }}>Save</button>
            </div>

          </div>
        </div>
      </main>
    </div>
  );
}


/* -------------------------------------
   STYLES — matches other seller pages
------------------------------------- */

const styles = {
  page: {
    fontFamily: "system-ui, sans-serif",
    color: "#111",
  },

  hero: {
    background: "#f6f7fbce",
    padding: "30px 0",
    borderRadius: "20px",
    width: "1100px",
    margin: "20px auto",
    boxShadow: "0 2px 8px rgba(0,0,0,0.05)",
  },

  heroInner: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    gap: 25,
  },

  title: {
    fontSize: 28,
    fontWeight: 700,
  },

  sectionCard: {
    width: "1000px",
    background: "white",
    borderRadius: 12,
    border: "1px solid #e7ebf0",
    padding: 25,
    boxShadow: "0 1px 3px rgba(0,0,0,0.07)",
  },

  infoGrid: {
    display: "grid",
    gridTemplateColumns: "repeat(2, 1fr)",
    gap: "10px 30px",
    fontSize: "17px",
    lineHeight: "28px",
    marginBottom: 20,
  },

  editBtn: {
    width: "100%",
    padding: "15px",
    background: "linear-gradient(135deg, #2f6feb, #1b4fba)",
    color: "white",
    border: "none",
    borderRadius: "10px",
    fontSize: "17px",
    fontWeight: 700,
    cursor: "pointer",
    marginBottom: "10px",
  },

  backBtn: {
    width: "100%",
    padding: "13px",
    background: "#ddd",
    borderRadius: "8px",
    border: "none",
    cursor: "pointer",
    fontSize: 15,
  },
};
