import { useMemo, useState } from "react";
import { Link } from "react-router-dom";
import flightsData from "./flightsData";
import "./HomePage.css"; // ✅ import your external stylesheet

export default function Homepage() {
  // ---- Query state ----
  const [from, setFrom] = useState("");
  const [to, setTo] = useState("");
  const [date, setDate] = useState("");
  const [passengers, setPassengers] = useState(1);
  const [airline, setAirline] = useState("All");
  const [nonstopOnly, setNonstopOnly] = useState(false);
  const [sortBy, setSortBy] = useState("priceAsc");

  // ---- Dummy flight data ----
  const flights = useMemo(() => flightsData, []);

  const airlineOptions = useMemo(
    () => ["All", ...Array.from(new Set(flights.map(f => f.airline)))],
    [flights]
  );

  const results = useMemo(() => {
    let out = flights.filter(f => {
      const matchFrom = from ? f.from.toLowerCase().includes(from.trim().toLowerCase()) : true;
      const matchTo = to ? f.to.toLowerCase().includes(to.trim().toLowerCase()) : true;
      const matchDate = date ? f.date === date : true;
      const matchAirline = airline === "All" ? true : f.airline === airline;
      const matchStops = nonstopOnly ? f.stops === 0 : true;
      return matchFrom && matchTo && matchDate && matchAirline && matchStops;
    });

    switch (sortBy) {
      case "priceAsc": out.sort((a, b) => a.price - b.price); break;
      case "priceDesc": out.sort((a, b) => b.price - a.price); break;
      case "durationAsc": out.sort((a, b) => a.durationMin - b.durationMin); break;
      case "departAsc": out.sort((a, b) => toMinutes(a.depart) - toMinutes(b.depart)); break;
      case "ratingDesc": out.sort((a, b) => b.rating - a.rating); break;
      default: break;
    }
    return out;
  }, [flights, from, to, date, airline, nonstopOnly, sortBy]);

  return (
    <div className="homepage">
      {/* ===== Header ===== */}
      <header className="homepage-header">
        <div className="homepage-container homepage-header-flex">
          <div className="brand">LiftOff</div>

          <nav className="nav">
            <Link className="nav-link" to="/check-in">Check-in</Link>
            <Link className="nav-link" to="/support">Customer-Support</Link>
            <Link className="nav-link" to="/UserDashboard">Your Profile</Link>
          </nav>

          <div className="auth-buttons">
            <Link to="/Login" className="nav-login-btn">Login</Link>
            <Link to="/SignUpPage" className="nav-signup-btn">Sign Up</Link>
          </div>
        </div>
      </header>

      {/* ===== Hero/Search ===== */}
      <main className="homepage-hero">
        <div className="homepage-container hero-inner">
          <form className="search-card" onSubmit={(e) => e.preventDefault()}>
            <div className="form-row">
              <label>
                From
                <input placeholder="e.g., JFK, EWR, ALB…" value={from} onChange={(e) => setFrom(e.target.value)} />
              </label>
              <label>
                To
                <input placeholder="e.g., LHR, SFO, MIA…" value={to} onChange={(e) => setTo(e.target.value)} />
              </label>
            </div>

            <div className="form-row">
              <label>
                Depart
                <input type="date" value={date} onChange={(e) => setDate(e.target.value)} />
              </label>
              <label>
                Passengers
                <input type="number" min="1" value={passengers} onChange={(e) => setPassengers(parseInt(e.target.value || "1", 10))} />
              </label>
            </div>

            <div className="form-row">
              <label>
                Airline
                <select value={airline} onChange={(e) => setAirline(e.target.value)}>
                  {airlineOptions.map(a => <option key={a} value={a}>{a}</option>)}
                </select>
              </label>
              <label>
                Sort by
                <select value={sortBy} onChange={(e) => setSortBy(e.target.value)}>
                  <option value="priceAsc">Price (low → high)</option>
                  <option value="priceDesc">Price (high → low)</option>
                  <option value="durationAsc">Duration (shortest)</option>
                  <option value="departAsc">Departure time (earliest)</option>
                  <option value="ratingDesc">Rating (best)</option>
                </select>
              </label>
            </div>

            <button className="clear-btn" type="submit">Clear Search</button>
          </form>

          <div className="results-count">{results.length} flights found</div>

          <div className="flight-results">
            {results.map(f => (
              <article key={f.id} className="flight-card">
                <div className="flight-info">
                  <div>
                    <div className="flight-airline">
                      {f.airline} <span className="badge">{f.flightNo}</span>
                    </div>
                    <div className="flight-route">{airportName(f.from)} → {airportName(f.to)}</div>
                  </div>
                  <div className="flight-times">
                    <div className="depart-time">{f.depart}</div>
                    <div className="city">{cityFromCode(f.from)}</div>
                  </div>
                  <div className="flight-duration">
                    <div>{formatDuration(f.durationMin)}</div>
                    <div>{f.stops === 0 ? "Nonstop" : `${f.stops} stop`}</div>
                  </div>
                  <div className="arrive-time">
                    <div>{f.arrive}</div>
                    <div className="city">{cityFromCode(f.to)}</div>
                  </div>
                  <div className="flight-price">
                    <div>${f.price.toFixed(2)}</div>
                    <div>★ {f.rating.toFixed(1)}</div>
                  </div>
                </div>
                <div className="flight-amenities">
                  {f.amenities.wifi && <span className="amenity">WiFi</span>}
                  {f.amenities.meals && <span className="amenity">Meals</span>}
                  {f.amenities.entertainment && <span className="amenity">Entertainment</span>}
                  <button className="select-btn">Select Flight</button>
                </div>
              </article>
            ))}
          </div>
        </div>
      </main>

      {/* ===== CTA Footer ===== */}
      <footer className="homepage-footer">
        <div className="footer-inner">
          <h3 className="footer-heading">Want to sell with us?</h3>
          <p className="footer-subtext">
            Join LiftOff’s partner program and reach more travelers worldwide.
          </p>

          <form className="footer-form">
            <input type="email" placeholder="Enter your business email" />
            <button
              onClick={(e) => {
                e.preventDefault();
                window.location.href = "/seller-signup";
              }}
            >
              Sign Up
            </button>
          </form>

          <small className="footer-note">
            Customer Support: +1 (518) 555-0123
          </small>
        </div>
      </footer>
    </div>
  );
}

/* ---- Helper functions ---- */
function toMinutes(hhmm) {
  const [h, m] = hhmm.split(":").map(Number);
  return h * 60 + m;
}
function formatDuration(min) {
  const h = Math.floor(min / 60);
  const m = min % 60;
  return `${h}h ${m}m`;
}
function cityFromCode(code) {
  const map = { JFK: "New York (JFK)", LGA: "New York (LGA)", EWR: "Newark (EWR)", LHR: "London (LHR)", SFO: "San Francisco (SFO)", MIA: "Miami (MIA)", LAX: "Los Angeles (LAX)", ALB: "Albany (ALB)", DFW: "Dallas (DFW)", DEN: "Denver (DEN)", CDG: "Paris (CDG)", MCO: "Orlando (MCO)" };
  return map[code] || code;
}
function airportName(code) {
  return code;
}
