import { useLocation, useParams, Link } from "react-router-dom";

export default function FlightDetails() {
  const { state } = useLocation();
  const { flight } = state || {};
  const { id } = useParams();

  if (!flight) return <div style={{ padding: 20 }}>No flight data found.</div>;

  return (
    <div style={styles.page}>

      {/* Main Content */}
      <main style={styles.hero}>
        <div style={{ ...styles.container, ...styles.heroInner }}>
          <h2 style={styles.title}>Flight Details</h2>

          {/* WIDER WHITE MAIN CARD */}
          <div style={styles.card}>
            <h3>{flight.airline} â€“ {flight.flightNo}</h3>

            <p><strong>From:</strong> {flight.from}</p>
            <p><strong>To:</strong> {flight.to}</p>
            <p><strong>Date:</strong> {flight.date}</p>
            <p><strong>Departure:</strong> {flight.depart}</p>
            <p><strong>Arrival:</strong> {flight.arrive}</p>
            <p><strong>Duration:</strong> {flight.durationMin} minutes</p>
            <p><strong>Stops:</strong> {flight.stops === 0 ? "Nonstop" : `${flight.stops} stop(s)`}</p>
            <p><strong>Rating:</strong> â˜… {flight.rating}</p>
            <p><strong>Price:</strong> ${flight.price.toFixed(2)}</p>

            <h4>Amenities</h4>
            <ul>
              {flight.amenities.wifi && <li>WiFi</li>}
              {flight.amenities.meals && <li>Meals</li>}
              {flight.amenities.entertainment && <li>Entertainment</li>}
            </ul>
          </div>

          {/* WIDER BOOKING BOX */}
          <div style={styles.bookBox}>
            <h3>Total: ${flight.price.toFixed(2)}</h3>
            <button style={styles.bookBtn}>Book Flight</button>
          </div>

        </div>
      </main>

      <footer style={styles.footer}>
        <div style={styles.container}><small>Customer Support: +1 (518) 555-0123</small></div>
      </footer>
    </div>
  );
}

// === UPDATED STYLES WITH WIDE CARDS ===
const styles = {
  page: { 
    fontFamily: "system-ui, -apple-system, Segoe UI, Roboto, sans-serif", 
    color: "#111" 
  },

  // ðŸ”¥ Wider layout overall
  container: { 
    maxWidth: 1200,  // was 900
    margin: "0 auto", 
    padding: "0 18px" 
  },

  // Transparent area (hero section)
  hero: {
    background: "#f6f7fbce",
    padding: "28px 0 40px",
    borderRadius: "20px",
    width: "100%",
    margin: "20px auto",
    boxShadow: "0 2px 8px rgba(0, 0, 0, 0.05)"
  },

  heroInner: { 
    display: "grid", 
    gap: 20 
  },

  title: { 
    margin: 0, 
    fontSize: 26, 
    fontWeight: 700 
  },

  // ðŸ”¥ MAIN WHITE CARD (flight detail card)
  card: {
    background: "white",
    border: "1px solid #e6e6e6",
    borderRadius: 14,
    padding: 24,
    display: "grid",
    gap: 12,
    boxShadow: "0 1px 2px rgba(0,0,0,.06)",

    maxWidth: "1000100px",
    width: "100%",
    margin: "0 auto"
  },

  // ðŸ”¥ BOOKING BOX â€” also wider to match
  bookBox: {
    background: "white",
    border: "1px solid #d4d8e0",
    borderRadius: 14,
    padding: 20,
    marginTop: 20,
    textAlign: "center",
    boxShadow: "0 1px 2px rgba(0,0,0,.05)",

    maxWidth: "1100px",
    width: "100%",
    margin: "0 auto"
  },

  bookBtn: {
    marginTop: 12,
    height: 42,
    background: "#2f6feb",
    border: "none",
    color: "#fff",
    width: "100%",
    borderRadius: 8,
    fontSize: 16,
    fontWeight: 700,
    cursor: "pointer"
  },

  footer: { 
    borderTop: "1px solid #eee", 
    padding: "16px 0", 
    color: "#666", 
    background: "#fff" 
  }
};
