import { useState } from "react";

export default function AddFlight() {
  const [flight, setFlight] = useState({
    airline: "",
    flightNo: "",
    from: "",
    to: "",
    depart: "",
    arrive: "",
    durationMin: "",
    stops: "",
    price: "",
    rating: "",

    // Airport info
    fromFull: "",
    fromFullAddress: "",
    terminalFrom: "",
    gateFrom: "",
    toFull: "",
    toFullAddress: "",
    terminalTo: "",
    gateTo: "",
    baggageClaim: "",

    // Local Times
    departureTimeLocal: "",
    arrivalTimeLocal: "",
    timezoneChange: "",

    // Baggage
    carryOnBagsAllowed: "",
    carryOnWeightLimitKg: "",
    personalItemAllowed: "",
    checkedBagsAllowed: "",
    checkedBagWeightLimitKg: "",
    extraBagFeeUSD: "",

    // Aircraft
    aircraftModel: "",
    aircraftCode: "",
    aircraftAgeYears: "",
    aircraftCapacity: "",
    seatLayout: "",
    seatPitch: "",
    seatWidth: "",
    hasUSBOutlets: "",
    hasPowerOutlets: "",
    hasLiveTV: "",

    // Performance
    onTimePercentage: "",
    averageDelayMin: "",
    cancellationRate: "",
    weatherDelayRiskScore: "",
    safetyScore: "",
  });

  const [errors, setErrors] = useState({});

  const handleChange = (e) => {
    setFlight({ ...flight, [e.target.name]: e.target.value });
    // Clear error for this field when user starts typing
    if (errors[e.target.name]) {
      setErrors({ ...errors, [e.target.name]: "" });
    }
  };

  // Validation function
  const validateForm = () => {
    const newErrors = {};

    // Required fields validation
    const requiredFields = [
      "airline",
      "flightNo",
      "from",
      "to",
      "depart",
      "arrive",
      "durationMin",
      "stops",
      "price",
      "rating",
      "fromFull",
      "fromFullAddress",
      "terminalFrom",
      "gateFrom",
      "toFull",
      "toFullAddress",
      "terminalTo",
      "gateTo",
      "baggageClaim",
      "departureTimeLocal",
      "arrivalTimeLocal",
      "timezoneChange",
      "carryOnBagsAllowed",
      "carryOnWeightLimitKg",
      "personalItemAllowed",
      "checkedBagsAllowed",
      "checkedBagWeightLimitKg",
      "extraBagFeeUSD",
      "aircraftModel",
      "aircraftCode",
      "aircraftAgeYears",
      "aircraftCapacity",
      "seatLayout",
      "seatPitch",
      "seatWidth",
      "hasUSBOutlets",
      "hasPowerOutlets",
      "hasLiveTV",
      "onTimePercentage",
      "averageDelayMin",
      "cancellationRate",
      "weatherDelayRiskScore",
      "safetyScore",
    ];

    requiredFields.forEach((field) => {
      if (!flight[field] || flight[field].toString().trim() === "") {
        newErrors[field] = "This field is required";
      }
    });

    // Airport code validation (3 letter codes)
    if (flight.from && !/^[A-Z]{3}$/.test(flight.from.toUpperCase())) {
      newErrors.from = "Airport code must be 3 uppercase letters (e.g., JFK)";
    }
    if (flight.to && !/^[A-Z]{3}$/.test(flight.to.toUpperCase())) {
      newErrors.to = "Airport code must be 3 uppercase letters (e.g., LAX)";
    }

    // Flight number validation (alphanumeric)
    if (flight.flightNo && !/^[A-Z0-9]{3,6}$/.test(flight.flightNo.toUpperCase())) {
      newErrors.flightNo = "Flight number must be 3-6 alphanumeric characters (e.g., DL245)";
    }

    // Numeric validations
    if (flight.durationMin && isNaN(flight.durationMin)) {
      newErrors.durationMin = "Duration must be a number";
    }
    if (flight.stops && isNaN(flight.stops)) {
      newErrors.stops = "Stops must be a number";
    }
    if (flight.price && (isNaN(flight.price) || parseFloat(flight.price) < 0)) {
      newErrors.price = "Price must be a positive number";
    }
    if (flight.rating && (isNaN(flight.rating) || parseFloat(flight.rating) < 1 || parseFloat(flight.rating) > 5)) {
      newErrors.rating = "Rating must be between 1 and 5";
    }
    if (flight.carryOnBagsAllowed && isNaN(flight.carryOnBagsAllowed)) {
      newErrors.carryOnBagsAllowed = "Must be a number";
    }
    if (flight.checkedBagsAllowed && isNaN(flight.checkedBagsAllowed)) {
      newErrors.checkedBagsAllowed = "Must be a number";
    }
    if (flight.carryOnWeightLimitKg && isNaN(flight.carryOnWeightLimitKg)) {
      newErrors.carryOnWeightLimitKg = "Must be a number";
    }
    if (flight.checkedBagWeightLimitKg && isNaN(flight.checkedBagWeightLimitKg)) {
      newErrors.checkedBagWeightLimitKg = "Must be a number";
    }
    if (flight.extraBagFeeUSD && (isNaN(flight.extraBagFeeUSD) || parseFloat(flight.extraBagFeeUSD) < 0)) {
      newErrors.extraBagFeeUSD = "Must be a positive number";
    }
    if (flight.aircraftAgeYears && isNaN(flight.aircraftAgeYears)) {
      newErrors.aircraftAgeYears = "Must be a number";
    }
    if (flight.aircraftCapacity && isNaN(flight.aircraftCapacity)) {
      newErrors.aircraftCapacity = "Must be a number";
    }
    if (flight.seatPitch && isNaN(flight.seatPitch)) {
      newErrors.seatPitch = "Must be a number";
    }
    if (flight.seatWidth && isNaN(flight.seatWidth)) {
      newErrors.seatWidth = "Must be a number";
    }
    if (flight.onTimePercentage && (isNaN(flight.onTimePercentage) || parseFloat(flight.onTimePercentage) < 0 || parseFloat(flight.onTimePercentage) > 100)) {
      newErrors.onTimePercentage = "Must be between 0 and 100";
    }
    if (flight.averageDelayMin && isNaN(flight.averageDelayMin)) {
      newErrors.averageDelayMin = "Must be a number";
    }
    if (flight.cancellationRate && (isNaN(flight.cancellationRate) || parseFloat(flight.cancellationRate) < 0 || parseFloat(flight.cancellationRate) > 100)) {
      newErrors.cancellationRate = "Must be between 0 and 100";
    }
    if (flight.weatherDelayRiskScore && (isNaN(flight.weatherDelayRiskScore) || parseFloat(flight.weatherDelayRiskScore) < 1 || parseFloat(flight.weatherDelayRiskScore) > 5)) {
      newErrors.weatherDelayRiskScore = "Must be between 1 and 5";
    }
    if (flight.safetyScore && (isNaN(flight.safetyScore) || parseFloat(flight.safetyScore) < 1 || parseFloat(flight.safetyScore) > 5)) {
      newErrors.safetyScore = "Must be between 1 and 5";
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    if (validateForm()) {
      console.log("Submitting Flight:", flight);
      alert("Flight Added Successfully!");
    }
  };

  return (
    <div style={styles.page}>
      <main style={styles.hero}>
        <div style={styles.heroInner}>

          <h2 style={styles.title}>Add New Flight</h2>

          {/* -------------------------------- */}
          {/* 1 SUMMARY CARD */}
          {/* -------------------------------- */}
          <div style={styles.summaryCard}>
            <div style={styles.summaryRow}>

              {/* Airline / Flight No / Rating */}
              <div>
                <label style={styles.label}>Airline</label>
                <input 
                  name="airline" 
                  placeholder="Delta Airlines"
                  value={flight.airline} 
                  onChange={handleChange} 
                  style={styles.input} 
                />

                <label style={styles.label}>Flight Number</label>
                <input 
                  name="flightNo"
                  placeholder="DL245"
                  value={flight.flightNo}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Rating </label>
                <input 
                  name="rating"
                  placeholder="4.6"
                  value={flight.rating}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* From / Depart */}
              <div>
                <label style={styles.label}>From (Airport Code)</label>
                <input 
                  name="from"
                  placeholder="JFK"
                  value={flight.from}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Departure Time</label>
                <input 
                  name="depart"
                  placeholder="08:30 AM"
                  value={flight.depart}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* Duration / Stops */}
              <div>
                <label style={styles.label}>Duration (min)</label>
                <input 
                  name="durationMin"
                  placeholder="180"
                  value={flight.durationMin}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Stops</label>
                <input 
                  name="stops"
                  placeholder="0"
                  value={flight.stops}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* To / Arrive */}
              <div>
                <label style={styles.label}>To (Airport Code)</label>
                <input 
                  name="to"
                  placeholder="LAX"
                  value={flight.to}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Arrival Time</label>
                <input 
                  name="arrive"
                  placeholder="11:15 AM"
                  value={flight.arrive}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* Price */}
              <div>
                <label style={styles.label}>Price ($)</label>
                <input 
                  name="price"
                  placeholder="299"
                  value={flight.price}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

            </div>
          </div>

          {/* -------------------------------- */}
          {/* 2 AIRPORT & ROUTE INFO */}
          {/* -------------------------------- */}
          <div style={styles.sectionCard}>
            <h3>Airport & Route Information</h3>

            <div style={styles.twoColumn}>
              <div>
                <h4 style={styles.subheading}>Departure Airport</h4>

                <label style={styles.label}>Full Name</label>
                <input 
                  name="fromFull"
                  placeholder="John F. Kennedy International Airport"
                  value={flight.fromFull}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Address</label>
                <input 
                  name="fromFullAddress"
                  placeholder="123 Airport Rd, New York, NY"
                  value={flight.fromFullAddress}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Terminal</label>
                <input 
                  name="terminalFrom"
                  placeholder="4"
                  value={flight.terminalFrom}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Gate</label>
                <input 
                  name="gateFrom"
                  placeholder="B22"
                  value={flight.gateFrom}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              {/* Arrival */}
              <div>
                <h4 style={styles.subheading}>Arrival Airport</h4>

                <label style={styles.label}>Full Name</label>
                <input 
                  name="toFull"
                  placeholder="Los Angeles International Airport"
                  value={flight.toFull}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Address</label>
                <input 
                  name="toFullAddress"
                  placeholder="1 World Way, Los Angeles, CA"
                  value={flight.toFullAddress}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Terminal</label>
                <input 
                  name="terminalTo"
                  placeholder="5"
                  value={flight.terminalTo}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Gate</label>
                <input 
                  name="gateTo"
                  placeholder="C19"
                  value={flight.gateTo}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Baggage Claim</label>
                <input 
                  name="baggageClaim"
                  placeholder="Carousel 7"
                  value={flight.baggageClaim}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>
            </div>

            <hr style={styles.separator} />

            <div style={styles.twoColumn}>
              <div>
                <label style={styles.label}>Departure Time (Local)</label>
                <input 
                  name="departureTimeLocal"
                  placeholder="08:30 AM EST"
                  value={flight.departureTimeLocal}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Arrival Time (Local)</label>
                <input 
                  name="arrivalTimeLocal"
                  placeholder="11:15 AM PST"
                  value={flight.arrivalTimeLocal}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>
            </div>

            <label style={styles.label}>Time Zone Change</label>
            <input 
              name="timezoneChange"
              placeholder="-3 hours"
              value={flight.timezoneChange}
              onChange={handleChange}
              style={styles.input} 
            />
          </div>

          {/* -------------------------------- */}
          {/* 3 BAGGAGE */}
          {/* -------------------------------- */}
          <div style={styles.sectionCard}>
            <h3>Baggage Information</h3>

            <div style={styles.twoColumn}>
              <div>
                <label style={styles.label}>Carry-On Bags Allowed</label>
                <input 
                  name="carryOnBagsAllowed"
                  placeholder="1"
                  value={flight.carryOnBagsAllowed}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Carry-On Weight (kg)</label>
                <input 
                  name="carryOnWeightLimitKg"
                  placeholder="10"
                  value={flight.carryOnWeightLimitKg}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Personal Item Allowed?</label>
                <input 
                  name="personalItemAllowed"
                  placeholder="Yes"
                  value={flight.personalItemAllowed}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Checked Bags Allowed</label>
                <input 
                  name="checkedBagsAllowed"
                  placeholder="2"
                  value={flight.checkedBagsAllowed}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Checked Bag Weight (kg)</label>
                <input 
                  name="checkedBagWeightLimitKg"
                  placeholder="23"
                  value={flight.checkedBagWeightLimitKg}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Extra Bag Fee ($)</label>
                <input 
                  name="extraBagFeeUSD"
                  placeholder="75"
                  value={flight.extraBagFeeUSD}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>
            </div>
          </div>

          {/* -------------------------------- */}
          {/* 4 AIRCRAFT */}
          {/* -------------------------------- */}
          <div style={styles.sectionCard}>
            <h3>Aircraft Information</h3>

            <div style={styles.twoColumn}>
              <div>
                <label style={styles.label}>Model</label>
                <input 
                  name="aircraftModel"
                  placeholder="Boeing 737"
                  value={flight.aircraftModel}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Registration</label>
                <input 
                  name="aircraftCode"
                  placeholder="N123AB"
                  value={flight.aircraftCode}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Age (years)</label>
                <input 
                  name="aircraftAgeYears"
                  placeholder="6"
                  value={flight.aircraftAgeYears}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Capacity</label>
                <input 
                  name="aircraftCapacity"
                  placeholder="180"
                  value={flight.aircraftCapacity}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Seat Layout</label>
                <input 
                  name="seatLayout"
                  placeholder="3-3"
                  value={flight.seatLayout}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Seat Pitch (inches)</label>
                <input 
                  name="seatPitch"
                  placeholder="31"
                  value={flight.seatPitch}
                  onChange={handleChange}
                  style={styles.input} 
                />

                <label style={styles.label}>Seat Width (inches)</label>
                <input 
                  name="seatWidth"
                  placeholder="17"
                  value={flight.seatWidth}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>
            </div>

            <hr style={styles.separator} />

            <div style={styles.twoColumn}>
              <div>
                <label style={styles.label}>USB Outlets?</label>
                <input 
                  name="hasUSBOutlets"
                  placeholder="Yes"
                  value={flight.hasUSBOutlets}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Power Outlets?</label>
                <input 
                  name="hasPowerOutlets"
                  placeholder="Yes"
                  value={flight.hasPowerOutlets}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Live TV?</label>
                <input 
                  name="hasLiveTV"
                  placeholder="Yes"
                  value={flight.hasLiveTV}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>
            </div>
          </div>

          {/* -------------------------------- */}
          {/* 5 PERFORMANCE */}
          {/* -------------------------------- */}
          <div style={styles.sectionCard}>
            <h3>Performance & Reliability</h3>

            <div style={styles.twoColumn}>
              <div>
                <label style={styles.label}>On-Time Rate (%)</label>
                <input 
                  name="onTimePercentage"
                  placeholder="88"
                  value={flight.onTimePercentage}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Average Delay (min)</label>
                <input 
                  name="averageDelayMin"
                  placeholder="12"
                  value={flight.averageDelayMin}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Cancellation Rate (%)</label>
                <input 
                  name="cancellationRate"
                  placeholder="1.5"
                  value={flight.cancellationRate}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>

              <div>
                <label style={styles.label}>Weather Delay Risk (15)</label>
                <input 
                  name="weatherDelayRiskScore"
                  placeholder="2"
                  value={flight.weatherDelayRiskScore}
                  onChange={handleChange}
                  style={styles.input} 
                />
              </div>
            </div>

            <label style={styles.label}>Safety Score (15)</label>
            <input 
              name="safetyScore"
              placeholder="5"
              value={flight.safetyScore}
              onChange={handleChange}
              style={styles.input} 
            />
          </div>

          {/* -------------------------------- */}
          {/* SUBMIT BUTTON */}
          {/* -------------------------------- */}
          <div style={styles.bookBox}>
            <button style={styles.bookBtn} onClick={handleSubmit}>
              Save Flight
            </button>
          </div>

        </div>
      </main>
    </div>
  );
}

