import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Admin.css';

export default function Admin() {
  const [tab, setTab] = useState('users');

  return (
    <div className="admin-page">
      <h1>Admin Dashboard</h1>
      <div className="admin-tabs">
        {['users', 'bookings', 'airlines', 'extras'].map(t => (
          <button key={t} className={`admin-tab ${tab===t? 'active':''}`} onClick={() => setTab(t)}>{t}</button>
        ))}
      </div>

      <div className="admin-body">
        {tab === 'users' && <AdminUsers />}
        {tab === 'bookings' && <AdminBookings />}
        {tab === 'airlines' && <AdminAirlines />}
        {tab === 'extras' && <AdminExtras />}
      </div>
    </div>
  );
}

function AdminUsers(){
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetch = async () => {
    try {
      const res = await axios.get('http://localhost:9000/admin/users');
      setUsers(res.data.users || []);
    } catch (err) {
      console.error('Failed to load users', err);
    } finally { setLoading(false); }
  }

  useEffect(() => { fetch(); }, []);

  const del = async (id) => {
    if (!confirm('Delete this user and their bookings?')) return;
    try {
      await axios.delete(`http://localhost:9000/admin/user/${id}`);
      setUsers(u => u.filter(x => x._id !== id));
    } catch (err) { console.error(err); alert('Failed to delete'); }
  }

  if (loading) return <p>Loading users...</p>;
  return (
    <div>
      <h2>Users</h2>
      <table className="admin-table">
        <thead><tr><th>Email</th><th>Name</th><th>Points</th><th>Actions</th></tr></thead>
        <tbody>
          {users.map(u => (
            <tr key={u._id}>
              <td>{u.username}</td>
              <td>{u.firstName} {u.lastName}</td>
              <td>{u.totalPoints || 0}</td>
              <td><button onClick={() => del(u._id)} className="danger">Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

function AdminBookings(){
  const [bookings, setBookings] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetch = async () => {
    try {
      const res = await axios.get('http://localhost:9000/admin/bookings');
      setBookings(res.data.bookings || []);
    } catch (err) { console.error('Failed to load bookings', err); }
    finally { setLoading(false); }
  }

  useEffect(() => { fetch(); }, []);

  const del = async (id) => {
    if (!confirm('Delete this booking?')) return;
    try {
      await axios.delete(`http://localhost:9000/admin/booking/${id}`);
      setBookings(b => b.filter(x=> x._id !== id));
    } catch (err) { console.error(err); alert('Failed to delete'); }
  }

  if (loading) return <p>Loading bookings...</p>;
  return (
    <div>
      <h2>Bookings</h2>
      <table className="admin-table">
        <thead><tr><th>Conf</th><th>Name</th><th>Route</th><th>Date</th><th>Price</th><th>Actions</th></tr></thead>
        <tbody>
          {bookings.map(b => (
            <tr key={b._id}>
              <td>{b.confirmationCode}</td>
              <td>{b.name} ({b.email})</td>
              <td>{b.from} → {b.to}</td>
              <td>{b.date}</td>
              <td>${b.price}</td>
              <td><button onClick={() => del(b._id)} className="danger">Delete</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  )
}

function AdminAirlines(){
  // For now show a placeholder and a link to flights listing
  const [flights, setFlights] = useState([]);
  useEffect(()=>{
    axios.get('http://localhost:9000/flights').then(r=>setFlights(r.data||[])).catch(()=>{});
  },[]);

  return (
    <div>
      <h2>Airlines / Flights</h2>
      <p>List of flights (editable in future):</p>
      <ul>
        {flights.map(f=> <li key={f._id}>{f.airline}: {f.from} → {f.to} ({f.price})</li>)}
      </ul>
    </div>
  )
}

function AdminExtras(){
  const [tickets, setTickets] = useState([]);
  useEffect(()=>{
    axios.get('http://localhost:9000/admin/tickets').then(r=>setTickets(r.data.tickets||[])).catch(()=>{});
  },[]);

  return (
    <div>
      <h2>Extras</h2>
      <p>Support tickets and simple stats will appear here.</p>
      <h3>Support Tickets</h3>
      {tickets.length===0? <p>No tickets</p> : (
        <ul>
          {tickets.map(t=> <li key={t._id}>{t.email}: {t.message}</li>)}
        </ul>
      )}
    </div>
  )
}
